import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/login', form);
      localStorage.setItem('token', res.data.access_token);
      setMessage('Успешный вход!');
    } catch (err) {
      setMessage('Ошибка входа: проверь email и пароль');
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: '400px' }}>
      <div className="card shadow">
        <div className="card-body">
          <h2 className="card-title text-center mb-4">Вход</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label>Email</label>
              <input name="email" type="email" className="form-control" onChange={handleChange} />
            </div>
            <div className="mb-3">
              <label>Пароль</label>
              <input name="password" type="password" className="form-control" onChange={handleChange} />
            </div>
            <button type="submit" className="btn btn-primary w-100">Войти</button>
          </form>
          {message && <div className="alert alert-info mt-3">{message}</div>}
        </div>
      </div>
    </div>
  );
}

export default Login;
