// frontend/src/pages/PlannerPage.jsx
import React, { useState } from 'react';
import ChatPanel from '../components/ChatPanel';
import TripVisualizer from '../components/TripVisualizer';

const sampleItinerary = [
  { day: 1, title: "Arrival in Almaty", activity: "Explore Almaty, enjoy local food", hotel: "Almaty Comfort Hostel", weather: "â˜€ï¸ Sunny, 22Â°C" },
  { day: 2, title: "Local Market & Museums", activity: "Visit Green Market and museums", hotel: "Hotel Kazakhstan", weather: "â›… Partly cloudy, 20Â°C" },
  { day: 3, title: "Mountain Adventure", activity: "Hiking in the Tian Shan mountains", hotel: "Mountain Eco Lodge", weather: "ðŸŒ„ Clear, 18Â°C" }
];

export default function PlannerPage() {
  const [messages, setMessages] = useState([
    { id: 1, from: 'bot', text: "ðŸ‘‹ Hello! I'm your smart travel planner. Let's start step-by-step! Where would you like to go?" }
  ]);
  const [itinerary, setItinerary] = useState([]);

  const sendMessage = text => {
    // 1) Ð´Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
    setMessages(msgs => [...msgs, { id: msgs.length + 1, from: 'user', text }]);

    // 2) Â«ÑÐ¸Ð¼ÑƒÐ»ÑÑ†Ð¸ÑÂ» Ð¾Ñ‚Ð²ÐµÑ‚Ð° Ð±Ð¾Ñ‚Ð°
    setTimeout(() => {
      const l = text.toLowerCase();
      if (l.includes('almaty')) {
        setMessages(msgs => [...msgs, { id: msgs.length + 1, from: 'bot', text: "Great! When are you planning to travel?" }]);
      } else if (/june|july|august/.test(l)) {
        setMessages(msgs => [...msgs, { id: msgs.length + 1, from: 'bot', text: "What do you want to do there?" }]);
      } else if (l.includes('explore')) {
        setMessages(msgs => [...msgs, { id: msgs.length + 1, from: 'bot', text: "Noted. Your itinerary is ready!" }]);
        setItinerary(sampleItinerary);
      } else {
        setMessages(msgs => [...msgs, { id: msgs.length + 1, from: 'bot', text: "Please specify your destination city." }]);
      }
    }, 800);
  };

  return (
    <div className="d-flex main-content" style={{ height: '100vh' }}>
      <div className="w-50 border-end p-3 overflow-auto">
        <ChatPanel messages={messages} onSend={sendMessage} />
      </div>
      <div className="w-50 p-3 overflow-auto">
        <TripVisualizer itinerary={itinerary} />
      </div>
    </div>
  );
}
