import { Link, useNavigate } from 'react-router-dom';
import { useUser } from '../context/UserContext';

function Navbar() {
  const { user, clearUser } = useUser();
  const navigate = useNavigate();

  const handleLogout = () => {
    clearUser();
    navigate('/login');
  };

  return (
<nav className="navbar navbar-expand-lg navbar-light bg-white border-bottom">
  <div className="container">
    <a className="navbar-brand fw-bold" href="/">Trip DVisor</a>
    <div className="collapse navbar-collapse">
      <ul className="navbar-nav ms-auto">
        <li className="nav-item">
          <a className="nav-link" href="/planner">Planner</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="/favorites">Favorites</a> {/* üëà –ù–æ–≤–æ–µ */}
        </li>
        <li className="nav-item">
          <button className="btn btn-outline-danger btn-sm ms-3">Sign Out</button>
