import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useUser } from '../context/UserContext';
import { useAuthRedirect } from '../hooks/useAuthRedirect';
import BurgerMenu from './BurgerMenu';
import './MobileMenu.css'; // для анимации

function Navbar() {
  const { user, clearUser } = useUser();
  const { goToPlanner } = useAuthRedirect();
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);

  const handleLogout = () => {
    clearUser();
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-light bg-white shadow-sm px-3 px-md-5 position-relative">
      <Link className="navbar-brand fw-bold d-flex align-items-center" to="/">
        <img src="/logo.png" alt="Trip DVisor Logo" height="30" className="me-2" />
      </Link>

      {/* Burger */}
      <div className="d-lg-none ms-auto">
        <BurgerMenu isOpen={isOpen} toggle={() => setIsOpen(!isOpen)} />
      </div>

      {/* Desktop Nav */}
      <div className="d-none d-lg-flex ms-auto align-items-center">
        <button className="btn btn-link nav-link" onClick={goToPlanner}>
          <i className="bi bi-robot me-1" /> Planner
        </button>
        <Link className="nav-link" to="/favorites">
          <i className="bi bi-heart me-1" /> Favorites
        </Link>
        {user ? (
          <button className="btn btn-outline-danger btn-sm" onClick={handleLogout}>
            Sign Out
          </button>
        ) : (
          <Link className="btn btn-outline-primary btn-sm" to="/login">Sign In</Link>
        )}
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <div className="mobile-menu shadow animate-slide">
          <button className="btn btn-link nav-link" onClick={() => { goToPlanner(); setIsOpen(false); }}>
            <i className="bi bi-robot me-1" /> Planner
          </button>
          <Link className="nav-link" to="/favorites" onClick={() => setIsOpen(false)}>
            <i className="bi bi-heart me-1" /> Favorites
          </Link>
          {user ? (
            <button className="btn btn-outline-danger btn-sm mt-2" onClick={() => { handleLogout(); setIsOpen(false); }}>
              Sign Out
            </button>
          ) : (
            <Link className="btn btn-outline-primary btn-sm mt-2" to="/login" onClick={() => setIsOpen(false)}>
              Sign In
            </Link>
          )}
        </div>
      )}
    </nav>
  );
}

export default Navbar;
