// frontend/src/components/TripVisualizer.jsx
import React from 'react';
import './TripVisualizer.css';

export default function TripVisualizer({ itinerary }) {
  if (!itinerary.length) {
    return <p className="text-muted">No itinerary data available.</p>;
  }
  return (
    <div className="trip-container">
      <h2 className="trip-title">üó∫ Your Itinerary</h2>
      <div className="trip-days">
        {itinerary.map(item => (
          <div key={item.day} className="trip-card">
            <div className="trip-day-label">Day {item.day}</div>
            <h5 className="trip-card-title">{item.title}</h5>
            <ul className="trip-details">
              <li>üìç <strong>Activity:</strong> {item.activity}</li>
              <li>üõè <strong>Hotel:</strong> {item.hotel}</li>
              <li>üå§ <strong>Weather:</strong> {item.weather}</li>
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}
