import React from 'react';

export default function TestTripPost() {
  const handleSubmit = async () => {
    const token = localStorage.getItem("token");

    if (!token) {
      alert("‚ùå No token found. Please log in first.");
      return;
    }

    const body = {
      name: "Trip to Rome",
      date_start: "2025-06-23",
      date_end: "2025-06-30"
    };

    try {
      const res = await fetch("http://localhost:5001/api/trips", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      const data = await res.json();
      console.log("üì° Response:", data);

      if (res.ok && data.success) {
        alert(`‚úÖ Trip created successfully! Trip ID: ${data.trip_id}`);
      } else {
        alert(`‚ùå Error: ${data.message || "Unknown error"}`);
      }
    } catch (err) {
      console.error("‚ùå Request failed:", err);
      alert("‚ùå Request failed (network error or server is down)");
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">üöÄ Test Trip POST</h2>
      <button
        className="btn btn-primary px-4 py-2 rounded"
        onClick={handleSubmit}
      >
        Send Test Trip
      </button>
    </div>
  );
}
