import React, { useEffect } from 'react';
import { useUser } from '../context/UserContext';
import { useNavigate, useLocation } from 'react-router-dom';

function HomePage() {
  const { user, setUser } = useUser();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const token = params.get('token');
    const username = params.get('username');

    if (token && username) {
      setUser({ token, username });
      localStorage.setItem('token', token);
      localStorage.setItem('username', username);
      navigate('/', { replace: true });
    }
  }, [location, navigate, setUser]);

  const handleCreateTrip = () => {
    navigate(user ? '/planner' : '/login');
  };

  return (
   <div className="container text-center" style={{ marginTop: '10vh', maxWidth: '900px' }}>
  <h1 className="display-4 fw-bold">Trip DVisor</h1>
  <h2 className="h4 text-muted mt-2">Welcome{user ? `, ${user.username}` : ''}!</h2>

  <p className="lead mt-4 mb-4">
    Trip DVisor is your intelligent travel companion. Create, customize, and optimize your travel plans using smart AI suggestions. Whether it's a solo trip or group adventure â€” we've got you covered!
  </p>

  <button
    className="btn btn-success btn-lg px-5 shadow-sm"
    onClick={handleCreateTrip}
  >
    <i className="bi bi-map-fill me-2"></i> Create a New Trip
  </button>

  <div className="row mt-5 justify-content-center">
    {[
      { icon: "bi-airplane-engines", label: "Choose your destination" },
      { icon: "bi-globe2", label: "AI plans step-by-step" },
      { icon: "bi-journal-check", label: "Get your itinerary" },
    ].map((item, idx) => (
      <div className="col-10 col-md-3 mb-4" key={idx}>
        <div className="card shadow-sm border-0 h-100 hover-effect p-4">
          <div className="text-primary mb-2">
            <i className={`bi ${item.icon} fs-2`}></i>
          </div>
          <div className="fw-semibold">{item.label}</div>
        </div>
      </div>
    ))}
  </div>

  <hr className="my-5" />

  <h4 className="fw-bold mb-4">Explore top destinations</h4>
  <div className="row justify-content-center">
    {[
      { city: "Paris", flag: "ðŸ‡«ðŸ‡·", img: "/images/paris.jpg" },
      { city: "Rome", flag: "ðŸ‡®ðŸ‡¹", img: "/images/rome.jpg" },
      { city: "Istanbul", flag: "ðŸ‡¹ðŸ‡·", img: "/images/istanbul.jpg" },
      { city: "Vienna", flag: "ðŸ‡¦ðŸ‡¹", img: "/images/vienna.jpg" },
    ].map((place, idx) => (
      <div className="col-6 col-md-3 mb-4" key={idx}>
        <div className="card shadow-sm border-0 h-100">
          <img src={place.img} className="card-img-top rounded-top" alt={place.city} />
          <div className="card-body text-center">
            <h6 className="mb-1">{place.city}</h6>
            <span className="text-muted">{place.flag}</span>
          </div>
        </div>
      </div>
    ))}
  </div>
</div>

      
  );
}

export default HomePage;
