import React, { useEffect, useState, useRef } from 'react';
import { Card, Button } from 'react-bootstrap';
import MapPreview from '../components/MapPreview';
import TripSummaryFooter from '../components/TripSummaryFooter';

function TripVisualizer() {
  const [showFooter, setShowFooter] = useState(false);
  const transportRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => setShowFooter(entry.isIntersecting),
      { threshold: 0.5 }
    );
    if (transportRef.current) observer.observe(transportRef.current);
    return () => observer.disconnect();
  }, []);

  return (
    <div className="position-relative px-4 py-4" style={{ backgroundColor: '#f9f9f9' }}>
      {/* Header */}
      <div className="d-flex justify-content-between align-items-start mb-4">
        <div>
          <h2 className="fw-bold">3-–¥–Ω–µ–≤–Ω–æ–µ –∫—É–ª—å—Ç—É—Ä–Ω–æ–µ –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ –≤ –†–∏–º</h2>
          <div className="mb-2 text-muted">üìÖ 29 May ‚Äì 1 Jun ‚Ä¢ üßç Solo traveler</div>
          <div>
            <span className="badge bg-primary me-2">–ú—É–∑–µ–∏</span>
            <span className="badge bg-info text-dark me-2">–ö–∞—Ñ–µ</span>
            <span className="badge bg-danger">–û–¥–∏–Ω–æ—á–Ω–æ–µ –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ</span>
          </div>
        </div>
        <div className="d-flex gap-2">
          <Button variant="outline-secondary" size="sm">üñä Modify</Button>
          <Button variant="outline-success" size="sm">üì• Download</Button>
          <Button variant="outline-info" size="sm">üîó Share</Button>
        </div>
      </div>

      {/* Overview */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold">Trip Overview</h5>
          <p>
            –ü–æ–≥—Ä—É–∑–∏—Ç–µ—Å—å –≤ <strong>–∫—É–ª—å—Ç—É—Ä–Ω–æ–µ –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ –ø–æ –†–∏–º—É</strong> –Ω–∞ 3 –¥–Ω—è —Å –ø–æ—Å–µ—â–µ–Ω–∏–µ–º –∏–∑–≤–µ—Å—Ç–Ω—ã—Ö –º—É–∑–µ–µ–≤, 
            –ø—Ä–æ–≥—É–ª–∫–∞–º–∏ –ø–æ –∏—Å—Ç–æ—Ä–∏—á–µ—Å–∫–æ–º—É —Ü–µ–Ω—Ç—Ä—É –∏ –æ—Ç–¥—ã—Ö–æ–º –≤ —É—é—Ç–Ω—ã—Ö –∫–∞—Ñ–µ. –í–∞—Å –∂–¥—ë—Ç —ç–∫—Å–∫—É—Ä—Å–∏—è –ø–æ –í–∞—Ç–∏–∫–∞–Ω—É, –ö–æ–ª–∏–∑–µ—é, –º–∞—Å—Ç–µ—Ä-–∫–ª–∞—Å—Å
            –ø–æ –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—é –ø–∞—Å—Ç—ã –∏ –º–Ω–æ–≥–æ–µ –¥—Ä—É–≥–æ–µ.
          </p>
        </Card.Body>
      </Card>

      {/* Highlights */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Highlights</h5>
          <ul className="list-unstyled">
            {[
              "‚úÖ –ü–æ—Å–µ—â–µ–Ω–∏–µ –í–∞—Ç–∏–∫–∞–Ω—Å–∫–∏—Ö –º—É–∑–µ–µ–≤ –∏ –°–∏–∫—Å—Ç–∏–Ω—Å–∫–æ–π –∫–∞–ø–µ–ª–ª—ã",
              "‚úÖ –ü—Ä–æ–≥—É–ª–∫–∞ –ø–æ –ü—å—è—Ü—Ü–∞ –ù–∞–≤–æ–Ω–∞ –∏ –§–æ–Ω—Ç–∞–Ω—É –¢—Ä–µ–≤–∏",
              "‚úÖ –ö—É–ª–∏–Ω–∞—Ä–Ω—ã–π –º–∞—Å—Ç–µ—Ä-–∫–ª–∞—Å—Å –ø–æ –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—é –ø–∞—Å—Ç—ã",
              "‚úÖ –≠–∫—Å–∫—É—Ä—Å–∏—è –ø–æ –ö–æ–ª–∏–∑–µ—é –∏ –†–∏–º—Å–∫–æ–º—É —Ñ–æ—Ä—É–º—É"
            ].map((line, i) => (
              <li key={i} className="mb-1">{line}</li>
            ))}
          </ul>
        </Card.Body>
      </Card>

      {/* Route Map */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Route Map</h5>
          <MapPreview />
        </Card.Body>
      </Card>

      {/* Itinerary */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Itinerary</h5>
          {[
            { day: 1, title: "–ü—Ä–∏–±—ã—Ç–∏–µ –≤ –†–∏–º –∏ –æ—Ç–¥—ã—Ö", date: "29 May" },
            { day: 2, title: "–í–∞—Ç–∏–∫–∞–Ω –∏ –∫–ª–∞—Å—Å–∏–∫–∞ –†–∏–º–∞", date: "30 May" },
            { day: 3, title: "–ò—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏–π —Ü–µ–Ω—Ç—Ä –∏ –∫—É–ª–∏–Ω–∞—Ä–∏—è", date: "31 May" },
            { day: 4, title: "–°–±–æ—Ä—ã –∏ –æ—Ç—ä–µ–∑–¥", date: "1 Jun" },
          ].map((item) => (
            <div key={item.day} className="mb-3">
              <div className="fw-semibold">üìÖ Day {item.day}: {item.title}</div>
              <div className="text-muted ms-3">{item.date}</div>
            </div>
          ))}
        </Card.Body>
      </Card>

      {/* Transportation */}
      <Card className="mb-4 shadow-sm" ref={transportRef}>
        <Card.Body>
          <h5 className="fw-bold mb-3">Transfer & Transportation</h5>

          <div className="mb-4">
            <div className="fw-semibold">üöó Milan ‚Üí Rome</div>
            <div className="text-muted small ms-4">Wed, Jul 2 ‚Ä¢ 5h 34m ‚Ä¢ Non-stop</div>
          </div>
          <div>
            <div className="fw-semibold">üöó Rome ‚Üí Milan</div>
            <div className="text-muted small ms-4">Sat, Jul 5 ‚Ä¢ 5h 21m ‚Ä¢ Non-stop</div>
