import React, { useEffect, useState, useRef } from 'react';
import { Card, Button } from 'react-bootstrap';
import MapPreview from '../components/MapPreview';
import TripSummaryFooter from '../components/TripSummaryFooter';

function TripVisualizer() {
  const [showFooter, setShowFooter] = useState(false);
  const transportRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => setShowFooter(entry.isIntersecting),
      { threshold: 0.5 }
    );
    if (transportRef.current) observer.observe(transportRef.current);
    return () => observer.disconnect();
  }, []);

  return (
    <div className="position-relative px-4 py-4" style={{ backgroundColor: '#f9f9f9' }}>
      {/* Header */}
      <div className="d-flex justify-content-between align-items-start mb-4">
        <div>
          <h2 className="fw-bold">3-дневное культурное путешествие в Рим</h2>
          <div className="mb-2 text-muted">📅 29 May – 1 Jun • 🧍 Solo traveler</div>
          <div>
            <span className="badge bg-primary me-2">Музеи</span>
            <span className="badge bg-info text-dark me-2">Кафе</span>
            <span className="badge bg-danger">Одиночное путешествие</span>
          </div>
        </div>
        <div className="d-flex gap-2">
          <Button variant="outline-secondary" size="sm">🖊 Modify</Button>
          <Button variant="outline-success" size="sm">📥 Download</Button>
          <Button variant="outline-info" size="sm">🔗 Share</Button>
        </div>
      </div>

      {/* Overview */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold">Trip Overview</h5>
          <p>
            Погрузитесь в <strong>культурное путешествие по Риму</strong> на 3 дня с посещением известных музеев, 
            прогулками по историческому центру и отдыхом в уютных кафе. Вас ждёт экскурсия по Ватикану, Колизею, мастер-класс
            по приготовлению пасты и многое другое.
          </p>
        </Card.Body>
      </Card>

      {/* Highlights */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Highlights</h5>
          <ul className="list-unstyled">
            {[
              "✅ Посещение Ватиканских музеев и Сикстинской капеллы",
              "✅ Прогулка по Пьяцца Навона и Фонтану Треви",
              "✅ Кулинарный мастер-класс по приготовлению пасты",
              "✅ Экскурсия по Колизею и Римскому форуму"
            ].map((line, i) => (
              <li key={i} className="mb-1">{line}</li>
            ))}
          </ul>
        </Card.Body>
      </Card>

      {/* Route Map */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Route Map</h5>
          <MapPreview />
        </Card.Body>
      </Card>

      {/* Itinerary */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Itinerary</h5>
          {[
            { day: 1, title: "Прибытие в Рим и отдых", date: "29 May" },
            { day: 2, title: "Ватикан и классика Рима", date: "30 May" },
            { day: 3, title: "Исторический центр и кулинария", date: "31 May" },
            { day: 4, title: "Сборы и отъезд", date: "1 Jun" },
          ].map((item) => (
            <div key={item.day} className="mb-3">
              <div className="fw-semibold">📅 Day {item.day}: {item.title}</div>
              <div className="text-muted ms-3">{item.date}</div>
            </div>
          ))}
        </Card.Body>
      </Card>

      {/* Transportation */}
      <Card className="mb-4 shadow-sm" ref={transportRef}>
        <Card.Body>
          <h5 className="fw-bold mb-3">Transfer & Transportation</h5>

          <div className="mb-4">
            <div className="fw-semibold">🚗 Milan → Rome</div>
            <div className="text-muted small ms-4">Wed, Jul 2 • 5h 34m • Non-stop</div>
          </div>
          <div>
            <div className="fw-semibold">🚗 Rome → Milan</div>
            <div className="text-muted small ms-4">Sat, Jul 5 • 5h 21m • Non-stop</div>
          </div>

          <div className="mt-3" style={{ width: '100%', height: '250px', borderRadius: '10px', background: '#f0f0f0' }}>
            <div className="d-flex justify-content-center align-items-center h-100 text-muted">
              Route map preview (future)
            </div>
          </div>
        </Card.Body>
      </Card>

      {/* Embedded Footer */}
      {showFooter && (
        <div className="mt-4">
          <TripSummaryFooter />
        </div>
      )}
    </div>
  );
}

export default TripVisualizer;
