import React, { useEffect, useState } from 'react';

function AccountPage() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [is2FAEnabled, setIs2FAEnabled] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    fetch('http://localhost:5001/api/user/profile', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
      credentials: 'include',
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setUsername(data.user.username);
          setEmail(data.user.email);
          setIs2FAEnabled(data.user.is_2fa_enabled);
        } else {
          setMessage(data.message || 'Failed to load');
        }
      });
  }, []);

  const handleSave = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('http://localhost:5001/api/user/profile', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ username, email }),
    });
    const data = await res.json();
    setMessage(data.message || (data.success ? 'Saved!' : 'Update failed.'));
  };

  const toggle2FA = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('http://localhost:5001/api/user/2fa/enable-disable', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ enable_2fa: !is2FAEnabled }),
    });
    const data = await res.json();
    if (data.success) {
      setIs2FAEnabled(prev => !prev);
      setMessage(data.message);
    } else {
      setMessage(data.message || 'Failed to update 2FA');
    }
  };

  return (
    <div className="container py-4" style={{ maxWidth: '500px' }}>
      <h3 className="mb-4">Account Settings</h3>
      {message && <div className="alert alert-info">{message}</div>}

      <div className="mb-3">
        <label>Username</label>
        <input className="form-control" value={username} onChange={e => setUsername(e.target.value)} />
      </div>

      <div className="mb-3">
        <label>Email</label>
        <input className="form-control" value={email} onChange={e => setEmail(e.target.value)} />
      </div>

      <div className="form-check form-switch my-3">
        <input className="form-check-input" type="checkbox" id="enable2fa"
          checked={is2FAEnabled} onChange={toggle2FA} />
        <label className="form-check-label" htmlFor="enable2fa">Enable 2FA</label>
      </div>

      <button className="btn btn-primary" onClick={handleSave}>Save Changes</button>
    </div>
  );
}

export default AccountPage;
