import React, { useEffect, useRef, useState } from 'react';

function ChatPanel() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages([...newMessages, { role: 'model', content: data.reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setMessages([...newMessages, { role: 'model', content: 'Error: could not get reply.' }]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetch('http://localhost:5000/api/ollama-reset', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' }
    });

    setMessages([{
      role: 'model',
      content: "\uD83D\uDC4B Hello! I'm your smart travel planner. Let's start step-by-step!\nWhere would you like to go?"
    }]);
  }, []);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="d-flex flex-column h-100 bg-white px-3 py-4">
      <div className="flex-grow-1 border rounded-4 p-3 bg-white shadow-sm overflow-auto mb-3">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-2 d-flex ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'}`}
          >
            <div
              className={`p-3 rounded-4 shadow-sm fw-normal fs-6 lh-base ${
                msg.role === 'user' ? 'bg-primary text-white' : 'bg-light text-dark'
              }`}
              style={{ maxWidth: '75%', whiteSpace: 'pre-wrap' }}
            >
              {msg.content}
            </div>
          </div>
        ))}
        {loading && <div className="text-muted text-center mt-2">LLaMA 3 is typing...</div>}
        <div ref={chatEndRef} />
      </div>

      <div className="d-flex align-items-center border rounded-pill px-3 py-2 shadow-sm" style={{ background: '#f7f7f7' }}>
        <input
          className="form-control border-0 bg-transparent me-2"
          type="text"
          placeholder="Type your message here..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          disabled={loading}
        />
        <button
          className="btn btn-light text-muted px-3 py-1 rounded-pill"
          onClick={handleSend}
          disabled={loading || !input.trim()}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatPanel;
