import React, { useRef, useState, useEffect } from 'react';
import { Card, Button } from 'react-bootstrap';
import MapPreview from '../components/MapPreview';
import TripSummaryFooter from '../components/TripSummaryFooter';

function TripVisualizer() {
  const mapRef = useRef(null);
  const [showFooter, setShowFooter] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setShowFooter(!entry.isIntersecting); // когда карта больше не видна — показываем футер
      },
      { threshold: 0.1 }
    );

    if (mapRef.current) {
      observer.observe(mapRef.current);
    }

    return () => {
      if (mapRef.current) {
        observer.unobserve(mapRef.current);
      }
    };
  }, []);

  return (
    <div className="px-4 py-4" style={{ backgroundColor: '#f9f9f9' }}>
      {/* Trip Header */}
      <div className="d-flex justify-content-between align-items-start mb-4">
        <div>
          <h2 className="fw-bold">3-дневное культурное путешествие в Рим</h2>
          <div className="mb-2 text-muted">📅 29 May – 1 Jun • 🧍 Solo traveler</div>
          <div>
            <span className="badge bg-primary me-2">Музеи</span>
            <span className="badge bg-info text-dark me-2">Кафе</span>
            <span className="badge bg-danger">Одиночное путешествие</span>
          </div>
        </div>
        <div className="d-flex gap-2">
          <Button variant="outline-secondary" size="sm">🖊 Modify</Button>
          <Button variant="outline-success" size="sm">📥 Download</Button>
          <Button variant="outline-info" size="sm">🔗 Share</Button>
        </div>
      </div>

      {/* Trip Overview */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold">Trip Overview</h5>
          <p>
            Погрузитесь в <strong>культурное путешествие по Риму</strong> на 3 дня с посещением известных музеев, 
            прогулками по историческому центру и отдыхом в уютных кафе...
          </p>
        </Card.Body>
      </Card>

      {/* Highlights */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Highlights</h5>
          <ul className="list-unstyled">
            <li>✅ Посещение Ватикана</li>
            <li>✅ Прогулка по Пьяцца Навона</li>
            <li>✅ Мастер-класс пасты</li>
            <li>✅ Колизей и Форум</li>
          </ul>
        </Card.Body>
      </Card>

      {/* Route Map */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Route Map</h5>
          <div ref={mapRef}>
            <MapPreview />
          </div>
        </Card.Body>
      </Card>

      {/* Itinerary */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Itinerary</h5>
          {[...Array(4)].map((_, idx) => (
            <div key={idx} className="mb-3">
              <div className="fw-semibold">📅 Day {idx + 1}: Sample title</div>
              <div className="text-muted ms-3">Date placeholder</div>
            </div>
          ))}
        </Card.Body>
      </Card>

      {/* Transfer */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Transfer & Transportation</h5>
          <div className="mb-3">
            🚗 Milan → Rome • 5h 34m
            <div className="text-muted small">Wed, Jul 2</div>
          </div>
          <div>
            🚗 Rome → Milan • 5h 21m
            <div className="text-muted small">Sat, Jul 5</div>
          </div>
        </Card.Body>
      </Card>

      {/* Footer appears only after map */}
      {showFooter && <TripSummaryFooter />}
    </div>
  );
}

export default TripVisualizer;
