import React, { useRef, useState, useEffect } from 'react';
import { Card, Button } from 'react-bootstrap';
import MapPreview from '../components/MapPreview';
import TripSummaryFooter from '../components/TripSummaryFooter';

function TripVisualizer() {
  const mapRef = useRef(null);
  const [showFooter, setShowFooter] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setShowFooter(!entry.isIntersecting); // –∫–æ–≥–¥–∞ –∫–∞—Ä—Ç–∞ –±–æ–ª—å—à–µ –Ω–µ –≤–∏–¥–Ω–∞ ‚Äî –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Ñ—É—Ç–µ—Ä
      },
      { threshold: 0.1 }
    );

    if (mapRef.current) {
      observer.observe(mapRef.current);
    }

    return () => {
      if (mapRef.current) {
        observer.unobserve(mapRef.current);
      }
    };
  }, []);

  return (
    <div className="px-4 py-4" style={{ backgroundColor: '#f9f9f9' }}>
      {/* Trip Header */}
      <div className="d-flex justify-content-between align-items-start mb-4">
        <div>
          <h2 className="fw-bold">3-–¥–Ω–µ–≤–Ω–æ–µ –∫—É–ª—å—Ç—É—Ä–Ω–æ–µ –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ –≤ –†–∏–º</h2>
          <div className="mb-2 text-muted">üìÖ 29 May ‚Äì 1 Jun ‚Ä¢ üßç Solo traveler</div>
          <div>
            <span className="badge bg-primary me-2">–ú—É–∑–µ–∏</span>
            <span className="badge bg-info text-dark me-2">–ö–∞—Ñ–µ</span>
            <span className="badge bg-danger">–û–¥–∏–Ω–æ—á–Ω–æ–µ –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ</span>
          </div>
        </div>
        <div className="d-flex gap-2">
          <Button variant="outline-secondary" size="sm">üñä Modify</Button>
          <Button variant="outline-success" size="sm">üì• Download</Button>
          <Button variant="outline-info" size="sm">üîó Share</Button>
        </div>
      </div>

      {/* Trip Overview */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold">Trip Overview</h5>
          <p>
            –ü–æ–≥—Ä—É–∑–∏—Ç–µ—Å—å –≤ <strong>–∫—É–ª—å—Ç—É—Ä–Ω–æ–µ –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ –ø–æ –†–∏–º—É</strong> –Ω–∞ 3 –¥–Ω—è —Å –ø–æ—Å–µ—â–µ–Ω–∏–µ–º –∏–∑–≤–µ—Å—Ç–Ω—ã—Ö –º—É–∑–µ–µ–≤, 
            –ø—Ä–æ–≥—É–ª–∫–∞–º–∏ –ø–æ –∏—Å—Ç–æ—Ä–∏—á–µ—Å–∫–æ–º—É —Ü–µ–Ω—Ç—Ä—É –∏ –æ—Ç–¥—ã—Ö–æ–º –≤ —É—é—Ç–Ω—ã—Ö –∫–∞—Ñ–µ...
          </p>
        </Card.Body>
      </Card>

      {/* Highlights */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Highlights</h5>
          <ul className="list-unstyled">
            <li>‚úÖ –ü–æ—Å–µ—â–µ–Ω–∏–µ –í–∞—Ç–∏–∫–∞–Ω–∞</li>
            <li>‚úÖ –ü—Ä–æ–≥—É–ª–∫–∞ –ø–æ –ü—å—è—Ü—Ü–∞ –ù–∞–≤–æ–Ω–∞</li>
            <li>‚úÖ –ú–∞—Å—Ç–µ—Ä-–∫–ª–∞—Å—Å –ø–∞—Å—Ç—ã</li>
            <li>‚úÖ –ö–æ–ª–∏–∑–µ–π –∏ –§–æ—Ä—É–º</li>
          </ul>
        </Card.Body>
      </Card>

      {/* Route Map */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Route Map</h5>
          <div ref={mapRef}>
            <MapPreview />
          </div>
        </Card.Body>
      </Card>

      {/* Itinerary */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Itinerary</h5>
          {[...Array(4)].map((_, idx) => (
            <div key={idx} className="mb-3">
              <div className="fw-semibold">üìÖ Day {idx + 1}: Sample title</div>
              <div className="text-muted ms-3">Date placeholder</div>
            </div>
          ))}
        </Card.Body>
      </Card>

      {/* Transfer */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Transfer & Transportation</h5>
          <div className="mb-3">
            üöó Milan ‚Üí Rome ‚Ä¢ 5h 34m
            <div className="text-muted small">Wed, Jul 2</div>
          </div>
          <div>
            üöó Rome ‚Üí Milan ‚Ä¢ 5h 21m
            <div className="text-muted small">Sat, Jul 5</div>
          </div>
        </Card.Body>
      </Card>

      {/* Footer appears only after map */}
      {showFooter && <TripSummaryFooter />}
    </div>
  );
}

export default TripVisualizer;
