import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './ChatPanel.css';

const STORAGE_KEY = 'plannerChatMessages';
const INITIAL_MESSAGES = [
  {
    role: 'assistant',
    content: "\u{1F44B} Hello! I'm your smart travel planner. Let's start step-by-step!\nWhere would you like to go?"
  }
];

export default function ChatPanel() {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    return saved ? JSON.parse(saved) : INITIAL_MESSAGES;
  });
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(messages));
  }, [messages]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${Math.min(textareaRef.current.scrollHeight, 150)}px`;
    }
  }, [input]);

  const clearChat = async () => {
    try {
      await fetch('http://localhost:5001/api/chat/reset', { method: 'POST' });
      localStorage.removeItem(STORAGE_KEY);
      setMessages(INITIAL_MESSAGES);
      setInput('');
    } catch (err) {
      console.error("Failed to reset chat on server:", err);
    }
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    const newMessages = [...messages, { role: 'user', content: input.trim() }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5001/api/perplexity-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input.trim() })
      });

      const data = await res.json();
      const reply = data.reply;
      setMessages([...newMessages, { role: 'assistant', content: reply }]);
    } catch (err) {
      console.error(err);
      setMessages([...newMessages, { role: 'assistant', content: '❌ Error: could not get reply.' }]);
    } finally {
      setLoading(false);
    }
  };

 return (
  <div className="chat-panel-container d-flex flex-column h-100">
    {/* Header: New Chat */}
    <div className="chat-header-wrapper px-3 pt-2 pb-1">
      <button className="chat-btn" onClick={clearChat} disabled={loading}>
        <span className="icon-circle"><i className="bi bi-plus-lg"></i></span>
        New Chat
      </button>
    </div>

    {/* Chat messages */}
    <div className="chat-messages flex-grow-1 overflow-auto px-3">
      {messages.map((msg, idx) => (
        <motion.div
          key={idx}
          className={`d-flex ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'} mb-2`}
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.2 }}
        >
          <div
            className={`chat-bubble shadow-sm ${msg.role === 'user' ? 'bg-primary text-white' : 'bg-white text-dark border'}`}
          >
            {msg.content}
          </div>
        </motion.div>
      ))}

      {/* Typing animation */}
      <AnimatePresence>
        {loading && (
          <motion.div className="d-flex justify-content-start mb-2" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
            <div className="chat-bubble bg-white text-dark d-flex align-items-center border">
              <div className="typing-loader"><span></span><span></span><span></span></div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
      <div ref={chatEndRef} />
    </div>

    {/* Input field */}
    <div className="chat-input-wrapper px-3 pt-1 pb-2">
      <div className="d-flex align-items-end gap-2 p-2 border rounded-4 bg-white shadow-sm">
        <textarea
          ref={textareaRef}
          className="form-control border-0 px-2 py-1"
          placeholder="Type your message here..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => (e.key === 'Enter' && !e.shiftKey ? (e.preventDefault(), handleSend()) : null)}
          rows={1}
          style={{
            resize: 'none',
            fontSize: '16px',
            boxShadow: 'none',
            outline: 'none',
            overflow: 'hidden',
            maxHeight: '150px',
            lineHeight: '1.5',
            flex: 1
          }}
        />
        <button
          className="btn btn-primary rounded-circle"
          style={{ width: '42px', height: '42px' }}
          onClick={handleSend}
          disabled={loading || !input.trim()}
        >
          <i className="bi bi-send-fill"></i>
        </button>
      </div>
    </div>
  </div>
);

}
