import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function MobileMenu({ user, goToPlanner, handleLogout, onClose }) {
  const [animateOut, setAnimateOut] = useState(false);

  const handleClose = () => {
    setAnimateOut(true);
    setTimeout(() => {
      onClose(); // реальное закрытие через родителя
    }, 300); // match with CSS duration
  };

  useEffect(() => {
    setAnimateOut(false); // сброс если меню переоткрывается
  }, []);

  return (
    <div
      className={`mobile-menu mobile-dropdown bg-white px-4 py-3 z-3 shadow position-absolute top-100 start-0 w-100 
      ${animateOut ? 'animate-slide-up' : 'animate-slide-down'}`}
    >
      <div className="d-flex justify-content-between align-items-center mb-3">
        <Link to="/" className="navbar-brand fw-bold d-flex align-items-center" onClick={handleClose}>
          <img src="/logo.png" alt="Trip DVisor Logo" height="30" className="me-2" />
        </Link>
        <button className="btn-close" onClick={handleClose} />
      </div>

      <button className="btn btn-link nav-link" onClick={() => { goToPlanner(); handleClose(); }}>
        <i className="bi bi-robot me-1" /> Planner
      </button>

      <Link className="nav-link" to="/favorites" onClick={handleClose}>
        <i className="bi bi-heart me-1" /> Favorites
      </Link>

      {user ? (
        <button className="btn btn-outline-danger btn-sm mt-2" onClick={() => { handleLogout(); handleClose(); }}>
          Sign Out
        </button>
      ) : (
        <Link className="btn btn-outline-primary btn-sm mt-2" to="/login" onClick={handleClose}>
          Sign In
        </Link>
      )}
    </div>
  );
}

export default MobileMenu;
