import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/login', form);
      const token = res.data.access_token;
      localStorage.setItem('token', token);
      setMessage('Успешный вход. Токен сохранён.');
    } catch (err) {
      setMessage('Ошибка входа: проверь email и пароль');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Вход</h2>
      <input name="email" type="email" placeholder="Email" onChange={handleChange} /><br />
      <input name="password" type="password" placeholder="Пароль" onChange={handleChange} /><br />
      <button type="submit">Войти</button>
      <p>{message}</p>
    </form>
  );
}

export default Login;
