import React from 'react';
import PhotoSide from '../components/PhotoSide';
import { Outlet } from 'react-router-dom';
import './AuthLayout.css'; // кастомные стили

function AuthLayout() {
  return (
    <div className="auth-layout d-flex flex-row vh-100 overflow-hidden">
      {/* Левая часть с фото — скрыта на моб. */}
      <div className="auth-photo d-none d-md-flex align-items-center justify-content-center">
        <PhotoSide />
      </div>

      {/* Правая часть с формой */}
      <div className="auth-form flex-grow-1 d-flex align-items-center justify-content-center">
        <Outlet />
      </div>
    </div>
  );
}

export default AuthLayout;
