import React from 'react';
import { Link } from 'react-router-dom';
import { useUser } from '../context/UserContext';
import { useAuthRedirect } from '../hooks/useAuthRedirect';
import './MobileMenu.css';

function MobileMenu({ onClose }) {
  const { user, clearUser } = useUser();
  const { goToPlanner } = useAuthRedirect();

  const handleLogout = () => {
    clearUser();
    onClose();
  };

  return (
    <>
      <div className="mobile-menu-backdrop" onClick={onClose} />
      <div className="mobile-menu-drawer animate-slide-down">
        <button className="btn-close ms-auto mb-2" onClick={onClose} />

        <button className="btn btn-link nav-link text-start" onClick={() => { goToPlanner(); onClose(); }}>
          <i className="bi bi-robot me-2" /> Planner
        </button>
        <Link className="nav-link text-start" to="/favorites" onClick={onClose}>
          <i className="bi bi-heart me-2" /> Favorites
        </Link>

        {user ? (
          <button className="btn btn-outline-danger w-100 mt-3" onClick={handleLogout}>
            Sign Out
          </button>
        ) : (
          <Link className="btn btn-outline-primary w-100 mt-3" to="/login" onClick={onClose}>
            Sign In
          </Link>
        )}
      </div>
    </>
  );
}

export default MobileMenu;
