import React, { useEffect, useState } from 'react';
import { useUser } from '../context/UserContext';

function AccountPage() {
  const { user } = useUser();
  const [email, setEmail] = useState('');
  const [enable2FA, setEnable2FA] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch('http://localhost:5001/api/account/settings', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      credentials: 'include',
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setEmail(data.data.email);
          setEnable2FA(data.data.enable_2fa);
        }
      })
      .catch(() => setMessage("Failed to load account info"));
  }, []);

  const handleSave = async () => {
    const res = await fetch('http://localhost:5001/api/account/settings', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      credentials: 'include',
      body: JSON.stringify({ enable_2fa: enable2FA }),
    });
    const data = await res.json();
    if (data.success) {
      setMessage("Settings updated!");
    } else {
      setMessage(data.message || "Update failed.");
    }
  };

  return (
    <div className="container py-5" style={{ maxWidth: '500px' }}>
      <h2 className="mb-4">Account Settings</h2>

      {message && <div className="alert alert-info">{message}</div>}

      <div className="mb-3">
        <label className="form-label">Username</label>
        <input type="text" className="form-control" value={user?.username || ''} disabled />
      </div>

      <div className="mb-3">
        <label className="form-label">Email</label>
        <input type="email" className="form-control" value={email} disabled />
      </div>

      <div className="form-check form-switch mb-4">
        <input
          className="form-check-input"
          type="checkbox"
          checked={enable2FA}
          onChange={(e) => setEnable2FA(e.target.checked)}
          id="enable2FA"
        />
        <label className="form-check-label" htmlFor="enable2FA">
          Enable Two-Factor Authentication (2FA)
        </label>
      </div>

      <button onClick={handleSave} className="btn btn-primary">
        Save Changes
      </button>
    </div>
  );
}

export default AccountPage;
