import React, { useRef, useEffect, useState } from 'react';
import { Card, Button } from 'react-bootstrap';
import MapPreview from '../components/MapPreview';
import TripSummaryFooter from '../components/TripSummaryFooter';

function TripVisualizer() {
  const mapRef = useRef(null);
  const [showFooter, setShowFooter] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setShowFooter(entry.isIntersecting);
      },
      { threshold: 0.6 } // –∫–æ–≥–¥–∞ –∫–∞—Ä—Ç–∞ –º–∏–Ω–∏–º—É–º –Ω–∞ 60% –≤–∏–¥–Ω–∞
    );

    if (mapRef.current) {
      observer.observe(mapRef.current);
    }

    return () => {
      if (mapRef.current) {
        observer.unobserve(mapRef.current);
      }
    };
  }, []);

  return (
    <div className="px-4 py-4" style={{ backgroundColor: '#f9f9f9' }}>
      {/* ... –ø—Ä–µ–¥—ã–¥—É—â–∏–µ —Å–µ–∫—Ü–∏–∏ (header, overview, highlights) ... */}

      <Card className="mb-4 shadow-sm" ref={mapRef}>
        <Card.Body>
          <h5 className="fw-bold mb-3">Route Map</h5>
          <MapPreview />
        </Card.Body>
      </Card>

      {/* –ë–ª–æ–∫ "Itinerary" */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Itinerary</h5>
          {[
            { day: 1, title: "–ü—Ä–∏–±—ã—Ç–∏–µ –≤ –†–∏–º –∏ –æ—Ç–¥—ã—Ö", date: "29 May" },
            { day: 2, title: "–í–∞—Ç–∏–∫–∞–Ω –∏ –∫–ª–∞—Å—Å–∏–∫–∞ –†–∏–º–∞", date: "30 May" },
            { day: 3, title: "–ò—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏–π —Ü–µ–Ω—Ç—Ä –∏ –∫—É–ª–∏–Ω–∞—Ä–∏—è", date: "31 May" },
            { day: 4, title: "–°–±–æ—Ä—ã –∏ –æ—Ç—ä–µ–∑–¥", date: "1 Jun" },
          ].map((item) => (
            <div key={item.day} className="mb-3">
              <div className="fw-semibold">üìÖ Day {item.day}: {item.title}</div>
              <div className="text-muted ms-3">{item.date}</div>
            </div>
          ))}
        </Card.Body>
      </Card>

      {/* Transfer block */}
      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <h5 className="fw-bold mb-3">Transfer & Transportation</h5>
          <div className="mb-4">
            <div className="fw-semibold">üöó Milan ‚Üí Rome</div>
            <div className="text-muted small ms-4">
              Wed, Jul 2 ‚Ä¢ 5h 34m ‚Ä¢ Non-stop
            </div>
          </div>
          <div>
            <div className="fw-semibold">üöó Rome ‚Üí Milan</div>
            <div className="text-muted small ms-4">
              Sat, Jul 5 ‚Ä¢ 5h 21m ‚Ä¢ Non-stop
            </div>
          </div>
          <div className="mt-3" style={{ width: '100%', height: '250px', borderRadius: '10px', background: '#f0f0f0' }}>
            <div className="d-flex justify-content-center align-items-center h-100 text-muted">
              Route map preview (future)
            </div>
          </div>
        </Card.Body>
      </Card>

      {/* üëá Footer –ø–æ—è–≤–∏—Ç—Å—è, —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –∫–∞—Ä—Ç–∞ –≤ –∑–æ–Ω–µ –≤–∏–¥–∏–º–æ—Å—Ç–∏ üëá */}
      {showFooter && <TripSummaryFooter />}
    </div>
  );
}

export default TripVisualizer;
