import React, { useEffect } from 'react';
import { useUser } from '../context/UserContext';
import { useNavigate, useLocation } from 'react-router-dom';

function HomePage() {
  const { user, setUser } = useUser();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const token = params.get('token');
    const username = params.get('username');

    if (token && username) {
      setUser({ token, username });
      localStorage.setItem('token', token);
      localStorage.setItem('username', username);
      navigate('/', { replace: true });
    }
  }, [location, navigate, setUser]);

  const handleCreateTrip = () => {
    navigate(user ? '/planner' : '/login');
  };

  return (
    <div className="container py-5 text-center mt-5 pt-5" style={{ marginTop: '120px' }}>
      <h1 className="display-4 fw-bold">Trip DVisor</h1>
      <h2 className="h3 fw-semibold mt-2">
        Welcome{user ? `, ${user.username}` : ''}!
      </h2>

      <p className="lead mt-4 mx-auto" style={{ maxWidth: '600px' }}>
               Trip DVisor is your intelligent travel companion.
        Create, customize, and optimize your travel plans using smart AI suggestions.
        Whether it's a solo trip or group adventure â€” we've got you covered!
      </p>

      <button
        className="btn btn-success btn-lg mt-4 px-5"
        onClick={handleCreateTrip}
      >
        <i className="bi bi-map-fill me-2"></i> Create a New Trip
      </button>

      <div className="row mt-5 justify-content-center">
        <div className="col-10 col-md-3 mb-3">
          <div className="card shadow-sm p-3">
            <div className="text-primary mb-2">
              <i className="bi bi-airplane-engines fs-2"></i>
            </div>
            <div className="fw-medium">Choose your destination</div>
          </div>
        </div>
        <div className="col-10 col-md-3 mb-3">
          <div className="card shadow-sm p-3">
            <div className="text-primary mb-2">
              <i className="bi bi-globe2 fs-2"></i>
            </div>
            <div className="fw-medium">AI plans step-by-step</div>
          </div>
        </div>
        <div className="col-10 col-md-3 mb-3">
          <div className="card shadow-sm p-3">
            <div className="text-primary mb-2">
              <i className="bi bi-journal-check fs-2"></i>
            </div>
            <div className="fw-medium">Get your itinerary</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
