import React, { useState, useEffect } from 'react';

const API_KEY = "pmNK8CZbn2lOEr8vN8OERavIp3CsrUkmKqVcx9wAte03zTloVzTv5EHX"; 

function PhotoSide() {
  const [photoUrl, setPhotoUrl] = useState('');

  const fetchPhoto = async () => {
    try {
      const res = await fetch(
        'https://api.pexels.com/v1/search?query=travel&per_page=1&page=' + Math.floor(Math.random() * 100),
        {
          headers: {
            Authorization: API_KEY
          }
        }
      );
      const data = await res.json();
      const img = data.photos?.[0]?.src?.landscape;
      if (img) setPhotoUrl(img);
    } catch (err) {
      console.error("Ошибка загрузки фото:", err);
    }
  };

  useEffect(() => {
    fetchPhoto();
    const interval = setInterval(() => {
      fetchPhoto();
    }, 60000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="col-md-6 d-none d-md-block"
      style={{
        backgroundImage: `url(${photoUrl})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh'
      }}
    />
  );
}

export default PhotoSide;
