import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useUser } from "./UserContext"; // если ты используешь context

export default function GoogleCallback() {
  const navigate = useNavigate();
  const { setUser } = useUser(); // или просто убери это, если не используешь

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get("token");
    const username = params.get("username");

    if (token && username) {
      localStorage.setItem("token", token);
      localStorage.setItem("username", username);

      if (setUser) setUser({ username, token }); // если есть context
      navigate("/planner");
    } else {
      navigate("/"); // ошибка — отправим на главную
    }
  }, [navigate]);

  return <p>Logging you in...</p>;
}
