import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useUser } from '../context/UserContext';

function Login() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState("");
  const [emailError, setEmailError] = useState("");
  const { setUser } = useUser();
  const navigate = useNavigate();

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));

    if (name === "email") {
      if (value === "") {
        setEmailError("");
      } else if (!emailRegex.test(value)) {
        setEmailError("Please enter a valid email address.");
      } else {
        setEmailError("");
      }
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError("");

    if (!emailRegex.test(form.email)) {
      setEmailError("Please enter a valid email address.");
      return;
    }

    try {
      const res = await fetch("http://localhost:5001/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
        credentials: "include"
      });

      const data = await res.json();
      console.log("SERVER RESPONSE:", data);

      if (data.success) {
        setUser({ email: form.email, username: data.username });
        navigate('/');
      } else {
        setError(data.message || "Login failed");
      }
    } catch (err) {
      setError("Network error. Try again.");
    }
  };

return (
  <div className="container-fluid min-vh-100 d-flex align-items-center justify-content-center">
    <div className="row w-100">
      {/* Левая панель с изображением или стилями — скрывается на мобилке */}
      <div className="col-md-6 d-none d-md-flex align-items-center justify-content-center bg-light">
        <img
          src="/your-login-image.jpg" // или promo banner, или картинка
          alt="Welcome"
          className="img-fluid p-4"
          style={{ maxHeight: "80vh", objectFit: "contain" }}
        />
      </div>

      {/* Правая часть с формой логина */}
      <div className="col-12 col-md-6 d-flex align-items-center justify-content-center">
        <form
          onSubmit={handleSubmit}
          className="w-100 p-4"
          style={{ maxWidth: '400px' }}
        >
          <h2 className="text-center mb-4 fw-bold">Welcome</h2>

          <div className="mb-3 d-grid">
            <a
              className="btn btn-outline-dark google-btn w-100"
              href="https://3955-192-167-110-47.ngrok-free.app"
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px'
              }}
            >
              <img
                src="https://developers.google.com/identity/images/g-logo.png"
                alt="Google"
                width="20"
                height="20"
              />
              Sign in with Google
            </a>
          </div>

          <hr className="my-4" style={{ opacity: 0.3 }} />

          <div className="mb-3">
            <label>Email</label>
            <input
              name="email"
              type="email"
              className={`form-control ${emailError ? "is-invalid" : ""}`}
              onChange={handleChange}
              required
            />
            {emailError && <div className="invalid-feedback">{emailError}</div>}
          </div>

          <div className="mb-3">
            <label>Password</label>
            <input
              name="password"
              type="password"
              className="form-control"
              onChange={handleChange}
              required
            />
          </div>

          {error && (
            <div className="alert alert-danger text-center" role="alert">
              {error}
            </div>
          )}

          <button type="submit" className="btn btn-primary w-100">
            Sign In
          </button>

          <p className="text-center mt-3">
            Don’t have an account? <Link to="/register">Sign up</Link>
          </p>
        </form>
      </div>
    </div>
  </div>
);
}
export default Login;
