import React, { useState, useEffect, useRef } from 'react';

function ChatPanel() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Автопрокрутка вниз
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // При первом запуске — автоматическое приветствие
  useEffect(() => {
    const startChat = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/ollama-chat', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ message: 'start' })
        });
        const data = await res.json();
        setMessages([{ role: 'assistant', content: data.reply }]);
      } catch (err) {
        console.error('Error starting chat:', err);
      }
    };

    startChat();
  }, []);

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      const reply = data.reply;

      setMessages([...newMessages, { role: 'assistant', content: reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setMessages([...newMessages, { role: 'assistant', content: '⚠️ Error: Could not get reply.' }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex flex-column h-100">
      <h4 className="mb-3">LLaMA 3 AI Chat</h4>

      <div className="flex-grow-1 border rounded p-3 bg-white shadow-sm overflow-auto mb-3" style={{ maxHeight: '70vh' }}>
        {messages.map((msg, idx) => (
          <div key={idx} className={`mb-2 ${msg.role === 'user' ? 'text-end' : 'text-start'}`}>
            <strong>{msg.role === 'user' ? 'You' : 'LLaMA 3'}:</strong> {msg.content}
          </div>
        ))}
        {loading && <div className="text-muted text-center mt-2">Thinking...</div>}
        <div ref={messagesEndRef} />
      </div>

      <div className="d-flex">
        <input
          className="form-control me-2"
          type="text"
          placeholder="Type your reply..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          disabled={loading}
        />
        <button className="btn btn-primary" onClick={handleSend} disabled={loading}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatPanel;
