import React, { useState, useEffect, useRef } from 'react';

const API_KEY = 'YOUR_PEXELS_API_KEY'; // üîÅ –í–°–¢–ê–í–¨ –°–í–û–ô API –ö–õ–Æ–ß –°–Æ–î–ê

function PhotoSide() {
  const [photoUrl, setPhotoUrl] = useState('');
  const intervalRef = useRef(null);

  const loadPhoto = async () => {
    try {
      const page = Math.floor(Math.random() * 100) + 1;
      const url = `https://api.pexels.com/v1/search?query=travel&orientation=portrait&size=large&per_page=1&page=${page}`;

      const res = await fetch(url, {
        headers: {
          Authorization: API_KEY,
        },
      });

      const data = await res.json();
      const photo = data.photos?.[0];

      if (photo && photo.src?.large2x) {
        const img = new Image();
        img.src = photo.src.large2x;

        img.onload = () => {
          setPhotoUrl(photo.src.large2x);
          console.log('‚úÖ New photo loaded:', photo.src.large2x);

          // –°—Ç–∞—Ä—Ç —Ü–∏–∫–ª–∞ –ø–æ—Å–ª–µ –ø–µ—Ä–≤–æ–π –∑–∞–≥—Ä—É–∑–∫–∏
          if (!intervalRef.current) {
            intervalRef.current = setInterval(() => {
              loadPhoto();
            }, 60000); // –∫–∞–∂–¥—ã–µ 60 —Å–µ–∫
          }
        };
      } else {
        console.log('‚õî No valid photo, retrying...');
        loadPhoto(); // –ø—Ä–æ–±—É–µ–º –¥—Ä—É–≥–æ–µ —Ñ–æ—Ç–æ
      }
    } catch (err) {
      console.error('‚ùå –û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ñ–æ—Ç–æ:', err);
    }
  };

  useEffect(() => {
    // –ü–µ—Ä–≤–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ —Å –Ω–µ–±–æ–ª—å—à–æ–π –∑–∞–¥–µ—Ä–∂–∫–æ–π
    const preload = setTimeout(() => {
      loadPhoto();
    }, 200);

    return () => {
      clearTimeout(preload);
      clearInterval(intervalRef.current);
    };
  }, []);

  return (
    <div
      className="col-md-6"
      style={{
        backgroundImage: photoUrl ? `url(${photoUrl})` : 'none',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',
        transition: 'background-image 1s ease-in-out',
      }}
    >
      {!photoUrl && (
        <div className="w-100 h-100 d-flex align-items-center justify-content-center bg-dark text-white">
          <p>–ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è...</p>
        </div>
      )}
    </div>
  );
}

export default PhotoSide;
