import React from 'react';
import { Card } from 'react-bootstrap';

function ItinerarySection() {
  const days = [
    { day: 1, title: "Arrival and Rest in Rome", date: "Jul 2" },
    { day: 2, title: "Ancient Rome & Colosseum", date: "Jul 3" },
    { day: 3, title: "Vatican and Culinary Experience", date: "Jul 4" },
    { day: 4, title: "Departure", date: "Jul 5" }
  ];

  return (
    <Card className="mb-4 shadow-sm">
      <Card.Body>
        <h5 className="fw-bold mb-3">Itinerary</h5>
        {days.map(({ day, title, date }) => (
          <div key={day} className="mb-3">
            <div className="fw-semibold">ðŸ“… Day {day}: {title}</div>
            <div className="text-muted ms-3">{date}</div>
          </div>
        ))}
      </Card.Body>
    </Card>
  );
}

export default ItinerarySection;
