import React, { useState, useEffect } from 'react';

const API_KEY = 'pmNK8CZbn2lOEr8vN8OERavIp3CsrUkmKqVcx9wAte03zTloVzTv5EHX'; // –í—Å—Ç–∞–≤—å –∫–ª—é—á

function PhotoSide() {
  const [photoUrl, setPhotoUrl] = useState('');
  const [nextUrl, setNextUrl] = useState('');

const preloadPhoto = async () => {
  try {
    const res = await fetch(
      'https://api.pexels.com/v1/search?query=travel&per_page=1&page=' + Math.floor(Math.random() * 100),
      {
        headers: {
          Authorization: API_KEY,
        },
      }
    );
    const data = await res.json();
    const photo = data.photos?.[0];

    if (photo && photo.src && photo.src.landscape) {
      const newImage = new Image();
      newImage.src = photo.src.landscape;

      newImage.onload = () => {
        console.log('Loaded new image:', photo.src.landscape);
        setPhotoUrl(photo.src.landscape); // üëà —Å—Ä–∞–∑—É –æ–±–Ω–æ–≤–ª—è–µ–º
      };
    }
  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–æ—Ç–æ:', error);
  }
};

  const switchPhoto = () => {
    if (nextUrl) {
      setPhotoUrl(nextUrl);
      setNextUrl('');
    }
  };

  useEffect(() => {
  let firstLoad = true;

  const loadAndSetPhoto = async () => {
    try {
      const res = await fetch(
        'https://api.pexels.com/v1/search?query=travel&per_page=1&page=' + Math.floor(Math.random() * 100),
        {
          headers: {
            Authorization: API_KEY,
          },
        }
      );
      const data = await res.json();
      const photo = data.photos?.[0];

      if (photo && photo.src?.landscape) {
        const img = new Image();
        img.src = photo.src.landscape;

        img.onload = () => {
          if (firstLoad) {
            setPhotoUrl(photo.src.landscape);
            firstLoad = false;
          } else {
            // –ø–ª–∞–≤–Ω–∞—è —Å–º–µ–Ω–∞
            setPhotoUrl(photo.src.landscape);
          }
        };
      }
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–æ—Ç–æ:', err);
    }
  };

  loadAndSetPhoto(); // –ø–µ—Ä–≤–∞—è –∑–∞–≥—Ä—É–∑–∫–∞

  const interval = setInterval(() => {
    loadAndSetPhoto();
  }, 60000); // –¥–∞–ª–µ–µ ‚Äî —Ä–∞–∑ –≤ 60 —Å–µ–∫

  return () => clearInterval(interval);
}, []);


  return (
    <div
      className="col-md-6"
      style={{
        backgroundImage: photoUrl ? `url(${photoUrl})` : 'none',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',
        transition: 'background-image 1s ease-in-out'
      }}
    >
      {/* fallback –Ω–∞ —Å–ª—É—á–∞–π, –µ—Å–ª–∏ —Ñ–æ—Ç–æ –Ω–µ—Ç */}
      {!photoUrl && (
        <div className="w-100 h-100 d-flex align-items-center justify-content-center bg-secondary text-white">
          <p>–ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è...</p>
        </div>
      )}
    </div>
  );
}

export default PhotoSide;
