// frontend/src/components/ChatPanel.jsx
import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const STORAGE_KEY = 'plannerChatMessages';
const INITIAL_MESSAGES = [
  {
    role: 'assistant',
    content: "\ud83d\udc4b Hello! I'm your smart travel planner. Let's start step-by-step!\nWhere would you like to go?"
  }
];

export default function ChatPanel() {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    return saved ? JSON.parse(saved) : INITIAL_MESSAGES;
  });
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(messages));
  }, [messages]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  const clearChat = async () => {
    try {
      await fetch('http://localhost:5001/api/chat/reset', { method: 'POST' });
      localStorage.removeItem(STORAGE_KEY);
      setMessages(INITIAL_MESSAGES);
      setInput('');
    } catch (err) {
      console.error("Failed to reset chat on server:", err);
    }
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    const newMessages = [...messages, { role: 'user', content: input.trim() }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5001/api/perplexity-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input.trim() })
      });

      const data = await res.json();
      const reply = data.reply;
      const extractSuggestions = (text) => {
        const hintPatterns = [
          /e\.g\.,?\s*((\*\*[^*]+\*\*,?\s*)+)/i,
          /Suggestions?:\s*((\*\*[^*]+\*\*,?\s*)+)/i,
          /consider:\s*((\*\*[^*]+\*\*,?\s*)+)/i,
          /popular (?:cities|options):\s*((\*\*[^*]+\*\*,?\s*)+)/i
        ];
        for (let pattern of hintPatterns) {
          const match = text.match(pattern);
          if (match) {
            return [...match[1].matchAll(/\*\*([^*]+)\*\*/g)].map(m => m[1]);
          }
        }
        return [];
      };
      const suggestedItems = extractSuggestions(reply);
      setSuggestions(suggestedItems.length > 0 ? suggestedItems : []);
      setMessages([...newMessages, { role: 'assistant', content: reply }]);
    } catch (err) {
      console.error(err);
      setMessages([...newMessages, { role: 'assistant', content: '❌ Error: could not get reply.' }]);
      setSuggestions([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex flex-column h-100 py-2" style={{ backgroundColor: '#f5f5f5' }}>
      <div className="mb-2">
        <button className="btn btn-outline-secondary w-100 rounded-pill" onClick={clearChat} disabled={loading}>
          New Chat
        </button>
      </div>

      <div className="flex-grow-1 overflow-auto mb-2" style={{ padding: '0.5rem', borderRadius: '12px' }}>
        {messages.map((msg, idx) => (
          <motion.div
            key={idx}
            className={`d-flex ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'} mb-2`}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.2 }}
          >
            <div
              className={`p-2 px-3 rounded-4 shadow-sm fw-normal fs-6 lh-base ${msg.role === 'user' ? 'bg-primary text-white' : 'bg-white text-dark border'}`}
              style={{ maxWidth: '80%', whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}
            >
              {msg.content}
            </div>
          </motion.div>
        ))}

        <AnimatePresence>
          {loading && (
            <motion.div className="d-flex justify-content-start mb-2" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
              <div className="p-2 px-3 rounded-4 bg-white text-dark fs-6 d-flex align-items-center border" style={{ maxWidth: '75%' }}>
                <div className="typing-loader"><span></span><span></span><span></span></div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
        <div ref={chatEndRef} />
      </div>

      <div className="d-flex align-items-end gap-2 p-2 border rounded-4 bg-white" style={{ boxShadow: '0 1px 5px rgba(0,0,0,0.05)' }}>
        <textarea
          className="form-control border-0 px-2 py-1"
          placeholder="Type your message here..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => (e.key === 'Enter' && !e.shiftKey ? (e.preventDefault(), handleSend()) : null)}
          rows={1}
          style={{ resize: 'none', fontSize: '16px', boxShadow: 'none', outline: 'none', overflow: 'hidden', maxHeight: '150px' }}
        />
        <button
          className="btn btn-primary rounded-circle"
          style={{ width: '42px', height: '42px' }}
          onClick={handleSend}
          disabled={loading || !input.trim()}
        >
          <i className="bi bi-send-fill"></i>
        </button>
      </div>

      <style>{`
        .typing-loader {
          display: flex;
          gap: 6px;
          align-items: center;
          height: 1rem;
        }
        .typing-loader span {
          width: 8px;
          height: 8px;
          background-color: #888;
          border-radius: 50%;
          display: inline-block;
          animation: typing 1s infinite ease-in-out;
        }
        .typing-loader span:nth-child(2) {
          animation-delay: 0.2s;
        }
        .typing-loader span:nth-child(3) {
          animation-delay: 0.4s;
        }

        @keyframes typing {
          0%, 80%, 100% {
            transform: scale(0.6);
            opacity: 0.3;
          }
          40% {
            transform: scale(1);
            opacity: 1;
          }
        }
      `}</style>
    </div>
  );
}
