import React, { useState, useEffect } from 'react';

const API_KEY = 'pmNK8CZbn2lOEr8vN8OERavIp3CsrUkmKqVcx9wAte03zTloVzTv5EHX'; // Вставь ключ

function PhotoSide() {
  const [photoUrl, setPhotoUrl] = useState('');
  const [nextUrl, setNextUrl] = useState('');

  const preloadPhoto = async () => {
    try {
      const res = await fetch(
        'https://api.pexels.com/v1/search?query=travel&per_page=1&page=' + Math.floor(Math.random() * 100),
        {
          headers: {
            Authorization: API_KEY,
          },
        }
      );
      const data = await res.json();
      const photo = data.photos?.[0];

      if (photo && photo.src && photo.src.landscape) {
        const newImage = new Image();
        newImage.src = photo.src.landscape;

        newImage.onload = () => {
          console.log('Loaded new image:', photo.src.landscape);
          setNextUrl(photo.src.landscape);
        };
      }
    } catch (error) {
      console.error('Ошибка загрузки фото:', error);
    }
  };

  const switchPhoto = () => {
    if (nextUrl) {
      setPhotoUrl(nextUrl);
      setNextUrl('');
    }
  };

  useEffect(() => {
    preloadPhoto();

    // Подгружаем за 2 сек до смены
    const preloadInterval = setInterval(preloadPhoto, 58000);
    const switchInterval = setInterval(switchPhoto, 60000);

    return () => {
      clearInterval(preloadInterval);
      clearInterval(switchInterval);
    };
  }, []);

  return (
    <div
      className="col-md-6"
      style={{
        backgroundImage: photoUrl ? `url(${photoUrl})` : 'none',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',
        transition: 'background-image 1s ease-in-out'
      }}
    >
      {/* fallback на случай, если фото нет */}
      {!photoUrl && (
        <div className="w-100 h-100 d-flex align-items-center justify-content-center bg-secondary text-white">
          <p>Загрузка изображения...</p>
        </div>
      )}
    </div>
  );
}

export default PhotoSide;
