import React, { useState, useEffect, useRef } from 'react';

function ChatPanel() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  useEffect(() => {
    setMessages([{
      role: 'model',
      content: "\uD83D\uDC4B Привет! Я ваш умный помощник по путешествиям. Давайте спланируем поездку шаг за шагом! Куда вы хотите отправиться?"
    }]);
  }, []);

  const handleSend = async () => {
    if (!input.trim()) return;
    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages([...newMessages, { role: 'model', content: data.reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setMessages([...newMessages, { role: 'model', content: '⚠️ Ошибка: не удалось получить ответ.' }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-white px-4 py-6">
      <div className="w-full max-w-2xl flex flex-col rounded-2xl border border-gray-200 shadow-xl p-4 bg-white">
        <div className="flex-1 overflow-y-auto space-y-4 max-h-[70vh] pr-2">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`rounded-xl px-4 py-3 text-base leading-relaxed whitespace-pre-wrap max-w-[80%] ${
                msg.role === 'user'
                  ? 'bg-blue-500 text-white self-end text-right'
                  : 'bg-gray-100 text-gray-900 self-start text-left'
              }`}
            >
              {msg.content}
            </div>
          ))}
          {loading && <div className="text-gray-400 text-sm">LLaMA 3 печатает...</div>}
          <div ref={messagesEndRef} />
        </div>

        <div className="mt-4 flex items-center gap-2 border-t pt-4">
          <input
            className="flex-1 border border-gray-300 rounded-full px-4 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
            type="text"
            placeholder="Введите ваш ответ..."
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
            disabled={loading}
          />
          <button
            className="bg-blue-500 text-white text-sm px-4 py-2 rounded-full hover:bg-blue-600 disabled:opacity-50"
            onClick={handleSend}
            disabled={loading}
          >
            Отправить
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatPanel;
