// frontend/src/components/ChatPanel.jsx
import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const STORAGE_KEY = 'plannerChatMessages';
const INITIAL_MESSAGES = [
  {
    role: 'assistant',
    content: "üëã Hello! I'm your smart travel planner. Let's start step-by-step!\nWhere would you like to go?"
  }
];

export default function ChatPanel() {
  const [messages, setMessages] = useState(() => {
    // –ø—Ä–∏ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏ –±–µ—Ä—ë–º –∏–∑ localStorage, –∏–ª–∏ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º INITIAL_MESSAGES
    const saved = localStorage.getItem(STORAGE_KEY);
    return saved ? JSON.parse(saved) : INITIAL_MESSAGES;
  });
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(messages));
  }, [messages]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

const clearChat = async () => {
  try {
    await fetch('http://localhost:5001/api/chat/reset', {
      method: 'POST'
    });
    localStorage.removeItem(STORAGE_KEY);
    setMessages(INITIAL_MESSAGES);
    setInput('');
  } catch (err) {
    console.error("Failed to reset chat on server:", err);
  }
};

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input.trim() }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5001/api/perplexity-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input.trim() })
      });
      const data = await res.json();
const updatedMessages = [...newMessages];

if (data.plan) {
  updatedMessages.push({ role: 'assistant', content: "‚úÖ Plan generated! Check the visualizer." });

  // üíæ –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤ localStorage
  localStorage.setItem("generatedPlan", JSON.stringify(data.plan));

  // üíæ –°–æ—Ö—Ä–∞–Ω—è–µ–º –Ω–∞ —É—Å—Ç—Ä–æ–π—Å—Ç–≤–æ
  const blob = new Blob([JSON.stringify(data.plan, null, 2)], { type: 'application/json' });
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = 'trip_plan.json';
  a.click();
  URL.revokeObjectURL(url);
} else {
  updatedMessages.push({ role: 'assistant', content: data.reply });
}

setMessages(updatedMessages);

    } catch (err) {
      console.error(err);
      setMessages([...newMessages, { role: 'assistant', content: '‚ùå Error: could not get reply.' }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex flex-column h-100 bg-white px-3 py-4">
      {/* –∫–Ω–æ–ø–∫–∞ New Chat */}
<div className="d-flex align-items-center gap-2 mb-3">
  <button
    className="btn new-chat-btn d-flex align-items-center gap-2"
    onClick={clearChat}
    disabled={loading}
  >
    <i className="bi bi-plus-circle-fill fs-5"></i>
    <span className="fw-medium">New Chat</span>
  </button>
    <button
    className="btn btn-outline-success btn-sm"
    onClick={async () => {
      setLoading(true);
      try {
        const res = await fetch('http://localhost:5001/api/perplexity-chat', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ message: "3 –¥–Ω—è –≤ –†–∏–º–µ, –º—É–∑–µ–∏, –∫–∞—Ñ–µ, –æ–¥–∏–Ω, 300 –µ–≤—Ä–æ" })
        });
        const data = await res.json();
        if (data.plan) {
          console.log("üì¶ JSON PLAN:", data.plan);  // –î–ª—è –æ—Ç–ª–∞–¥–∫–∏
          setMessages(prev => [...prev, {
            role: 'assistant',
            content: `üì¶ Plan JSON received:\n\n` + JSON.stringify(data.plan, null, 2)
          }]);
        } else {
          setMessages(prev => [...prev, {
            role: 'assistant',
            content: data.reply || 'No structured plan.'
          }]);
        }
      } catch (err) {
        console.error("‚ùå Generate failed:", err);
        setMessages(prev => [...prev, {
          role: 'assistant',
          content: '‚ùå Error generating plan.'
        }]);
      } finally {
        setLoading(false);
      }
    }}
    disabled={loading}
  >
    Generate Plan
  </button>
</div>

      {/* –æ–∫–Ω–æ –ø–µ—Ä–µ–ø–∏—Å–∫–∏ */}
      <div className="flex-grow-1 border rounded-4 p-3 bg-white shadow-sm overflow-auto mb-3">
        {messages.map((msg, idx) => (
          <motion.div
            key={idx}
            className={`mb-2 d-flex ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'}`}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.2 }}
          >
            <div
              className={`p-3 rounded-4 shadow-sm fw-normal fs-6 lh-base ${
                msg.role === 'user' ? 'bg-primary text-white' : 'bg-light text-dark'
              }`}
              style={{ maxWidth: '75%', whiteSpace: 'pre-wrap' }}
            >
              {msg.content}
            </div>
          </motion.div>
        ))}

        {/* –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä ¬´typing...¬ª */}
        <AnimatePresence>
          {loading && (
            <motion.div
              className="d-flex justify-content-start mb-2"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <div
                className="p-3 rounded-4 shadow-sm bg-light text-dark fs-6 d-flex align-items-center"
                style={{ maxWidth: '75%' }}
              >
                <div className="typing-loader">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        <div ref={chatEndRef} />
      </div>

      {/* —Ñ–æ—Ä–º–∞ –≤–≤–æ–¥–∞ */}
      <div className="d-flex align-items-center justify-content-between rounded-4 px-4 py-3 shadow-sm border bg-white" style={{ gap: '1rem' }}>
        <input
          className="form-control border-0 bg-white fs-6"
          type="text"
          placeholder="Type your message here..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          disabled={loading}
          style={{ flexGrow: 1 }}
        />
        <button
          className="btn btn-primary px-4 py-2 rounded-3"
          onClick={handleSend}
          disabled={loading || !input.trim()}
        >
          Send
        </button>
      </div>

      {/* –°—Ç–∏–ª–∏ –¥–ª—è dots-typing */}
      <style>{`
        .typing-loader {
          display: flex;
          gap: 6px;
          align-items: center;
        }
        .typing-loader span {
          width: 8px;
          height: 8px;
          background-color: #888;
          border-radius: 50%;
          display: inline-block;
          animation: typing 1s infinite ease-in-out;
        }
        .typing-loader span:nth-child(2) { animation-delay: 0.2s; }
        .typing-loader span:nth-child(3) { animation-delay: 0.4s; }
        @keyframes typing {
          0%, 80%, 100% { transform: scale(0.8); opacity: 0.5; }
          40% { transform: scale(1.2); opacity: 1; }
        }
          .new-chat-btn {
  background-color: #ffffff;
  border: 1px solid #dee2e6;
  border-radius: 12px;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.08);
  padding: 0.5rem 1rem;
  transition: all 0.2s ease;
  font-size: 0.95rem;
}

.new-chat-btn:hover {
  background-color: #f8f9fa;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

      `}</style>
    </div>
  );
}
