import React, { useEffect, useState } from 'react';
import { useUser } from '../context/UserContext';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuthRedirect } from '../hooks/useAuthRedirect';

function HomePage() {
  const { setUser } = useUser();
  const navigate = useNavigate();
  const location = useLocation();
  const { goToPlanner } = useAuthRedirect();
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };
    handleResize(); // check on mount
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const token = params.get('token');
    const username = params.get('username');

    if (token && username) {
      setUser({ token, username });
      localStorage.setItem('token', token);
      localStorage.setItem('username', username);
      navigate('/', { replace: true });
    }
  }, [location, navigate, setUser]);

  const username = localStorage.getItem('username');

  const mobileCards = [
    { icon: 'bi-airplane-engines', text: 'Pick destination' },
    { icon: 'bi-globe2', text: 'AI step-by-step' },
    { icon: 'bi-journal-check', text: 'Get itinerary' },
  ];

  const desktopCards = [
    { icon: 'bi-airplane-engines', text: 'Choose your destination' },
    { icon: 'bi-globe2', text: 'AI plans step-by-step' },
    { icon: 'bi-journal-check', text: 'Get your itinerary' },
  ];

  return (
    <div className="container py-5 text-center" style={{ marginTop: '10vh' }}>
      <h1 className="display-5 fw-bold">Trip DVisor</h1>
      <h2 className="h4 fw-semibold mt-2">
        Welcome{username ? `, ${username}` : ''}!
      </h2>

{isMobile ? (
  <>
    <div className="mt-3 mb-4">
      <h2 className="h5 fw-semibold">Hi{username ? `, ${username}` : ''} ðŸ‘‹</h2>
      <p className="text-muted mb-2">Ready to plan your next trip?</p>
    </div>

    <button className="btn btn-success btn-lg w-100 py-2 mb-4 shadow-sm" onClick={goToPlanner}>
      <i className="bi bi-compass me-2"></i> Start Planning
    </button>

    <div className="d-flex justify-content-between gap-3 mb-4">
      {mobileCards.map(({ icon, text }, idx) => (
        <div
          key={idx}
          className="flex-fill border rounded text-center py-3 shadow-sm"
          style={{ minWidth: '0' }}
        >
          <i className={`bi ${icon} text-primary fs-3 d-block mb-2`}></i>
          <small className="fw-medium">{text}</small>
        </div>
      ))}
    </div>

    <div className="bg-light rounded shadow-sm p-3 text-start">
      <strong className="text-primary d-block mb-1">
        ðŸ’¡ Tip of the Day
      </strong>
      <span className="small text-muted">
        Donâ€™t forget to check visa rules before booking your flight!
      </span>
    </div>
  </>
) : (

  <>
    {/* ... */}
  </>
)}

    </div>
  );
}

export default HomePage;
