import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/login', form);
      localStorage.setItem('token', res.data.access_token);
      setMessage('Успешный вход!');
    } catch (err) {
      setMessage('Ошибка входа');
    }
  };

  return (
    <div className="col-md-6 d-flex align-items-center justify-content-center">
      <form onSubmit={handleSubmit} className="p-4" style={{ width: '100%', maxWidth: '400px' }}>
        <h2 className="text-center mb-4">Welcome</h2>
        <div className="mb-3">
          <label>Email</label>
          <input name="email" type="email" className="form-control" onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label>Password</label>
          <input name="password" type="password" className="form-control" onChange={handleChange} />
        </div>
        <button type="submit" className="btn btn-primary w-100">Login</button>
        {message && <div className="alert alert-info mt-3">{message}</div>}
        <p className="text-center mt-3">
          Don't have an account? <Link to="/register">Register now</Link>
        </p>
      </form>
    </div>
  );
}

export default Login;
