import React, { useEffect, useRef, useState } from 'react';
import { motion } from 'framer-motion';

function ChatPanel() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  // 📌 Статическое приветствие при загрузке
  useEffect(() => {
    setMessages([{ role: 'model', content: "👋 Hello! I'm your smart travel planner. Let's start step-by-step!\nWhere would you like to go?" }]);
  }, []);

  // 🔽 Автоскролл
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages([...newMessages, { role: 'model', content: data.reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setMessages([...newMessages, { role: 'model', content: 'Error: could not get reply.' }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex flex-column h-100 p-3 bg-light" style={{ maxWidth: '800px', margin: '0 auto' }}>
      <h4 className="mb-3 text-center">LLaMA 3 AI Chat</h4>

      <div className="flex-grow-1 border rounded p-3 bg-white shadow-sm overflow-auto mb-3" style={{ minHeight: '400px' }}>
        {messages.map((msg, idx) => (
          <motion.div
            key={idx}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            className={`mb-3 px-3 py-2 rounded shadow-sm ${msg.role === 'user' ? 'bg-primary text-white ms-auto' : 'bg-secondary-subtle text-dark me-auto'}`}
            style={{ maxWidth: '75%', whiteSpace: 'pre-wrap' }}
          >
            {msg.content}
          </motion.div>
        ))}
        {loading && (
          <div className="text-muted text-center mb-2" style={{ fontSize: '0.9em' }}>🟡🟡🟡</div>
        )}
        <div ref={chatEndRef} />
      </div>

      <div className="d-flex align-items-center border rounded-pill px-3 py-2 shadow-sm bg-white">
        <input
          className="form-control border-0 shadow-none bg-transparent"
          type="text"
          placeholder="Type your answer..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          style={{ flex: 1 }}
        />
        <button
          className="btn btn-outline-primary ms-2 rounded-pill px-3"
          onClick={handleSend}
          disabled={loading}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatPanel;
