import React, { useState, useEffect, useRef } from 'react';

function ChatPanel() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  useEffect(() => {
    setMessages([{
      role: 'model',
      content: "\uD83D\uDC4B Привет! Я ваш умный помощник по путешествиям. Давайте спланируем поездку шаг за шагом! Куда вы хотите отправиться?"
    }]);
  }, []);

  const handleSend = async () => {
    if (!input.trim()) return;
    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages([...newMessages, { role: 'model', content: data.reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setMessages([...newMessages, { role: 'model', content: '⚠️ Ошибка: не удалось получить ответ.' }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gradient-to-br from-blue-50 to-white p-4">
      <div className="max-w-3xl mx-auto w-full bg-white rounded-2xl shadow-lg p-6 flex flex-col h-full">
        <div className="text-xl font-semibold mb-4 text-gray-800">AI Travel Assistant</div>

        <div className="flex-1 overflow-y-auto space-y-3 p-2">
          {messages.map((msg, idx) => (
            <div key={idx} className={`rounded-xl px-4 py-2 max-w-[80%] whitespace-pre-wrap ${msg.role === 'user' ? 'bg-blue-100 self-end text-right' : 'bg-gray-100 self-start text-left'}`}>
              <strong>{msg.role === 'user' ? 'Вы' : 'LLaMA 3'}:</strong> {msg.content}
            </div>
          ))}
          {loading && <div className="text-sm text-gray-500">LLaMA 3 печатает...</div>}
          <div ref={messagesEndRef} />
        </div>

        <div className="mt-4 flex items-center gap-2">
          <input
            className="flex-1 border border-gray-300 rounded-xl px-4 py-2 focus:outline-none focus:ring focus:ring-blue-300"
            type="text"
            placeholder="Введите ваш ответ..."
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
            disabled={loading}
          />
          <button
            className="bg-blue-500 text-white rounded-xl px-4 py-2 hover:bg-blue-600 disabled:opacity-50"
            onClick={handleSend}
            disabled={loading}
          >
            Отправить
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatPanel;
