import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useUser } from '../context/UserContext';
import { useAuthRedirect } from '../hooks/useAuthRedirect';
import BurgerMenu from './BurgerMenu';

function Navbar() {
  const { user, clearUser } = useUser();
  const { goToPlanner } = useAuthRedirect();
  const navigate = useNavigate();
  const [isCollapsed, setIsCollapsed] = useState(true);

  const handleLogout = () => {
    clearUser();
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm px-3 px-md-5">
      <Link className="navbar-brand fw-bold d-flex align-items-center" to="/">
        <img src="/logo.png" alt="Trip DVisor Logo" height="30" className="me-2" />
      </Link>

      {/* Custom Burger Menu Button */}
      <div className="d-lg-none ms-auto">
        <BurgerMenu isOpen={!isCollapsed} toggle={() => setIsCollapsed(!isCollapsed)} />
      </div>

      {/* Collapsible nav content */}
      <div className={`collapse navbar-collapse ${!isCollapsed ? 'show' : ''}`} id="navbarContent">
        <ul className="navbar-nav ms-auto align-items-lg-center">
          <li className="nav-item">
            <button className="btn btn-link nav-link" onClick={goToPlanner}>
              <i className="bi bi-robot me-1" /> Planner
            </button>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/favorites">
              <i className="bi bi-heart me-1" /> Favorites
            </Link>
          </li>
          <li className="nav-item mt-2 mt-lg-0">
            {user ? (
              <button className="btn btn-outline-danger btn-sm" onClick={handleLogout}>
                Sign Out
              </button>
            ) : (
              <Link className="btn btn-outline-primary btn-sm" to="/login">
                Sign In
              </Link>
            )}
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
