import React, { useEffect, useRef, useState } from 'react';
import { motion } from 'framer-motion';

function ChatPanel() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [isScrolledUp, setIsScrolledUp] = useState(false);
  const chatContainerRef = useRef(null);
  const chatEndRef = useRef(null);

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages([...newMessages, { role: 'model', content: data.reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setMessages([...newMessages, { role: 'model', content: 'Error: could not get reply.' }]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetch('http://localhost:5000/api/ollama-reset', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' }
    });

    setMessages([{
      role: 'model',
      content: "\uD83D\uDC4B Hello! I'm your smart travel planner. Let's start step-by-step!\nWhere would you like to go?"
    }]);
  }, []);

  useEffect(() => {
    if (!isScrolledUp) {
      chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages, loading]);

  const handleScroll = () => {
    const container = chatContainerRef.current;
    if (!container) return;

    const bottomReached = container.scrollHeight - container.scrollTop <= container.clientHeight + 30;
    setIsScrolledUp(!bottomReached);
  };

  return (
    <div className="d-flex flex-column h-100 bg-white px-3 py-4">
      <div
        ref={chatContainerRef}
        onScroll={handleScroll}
        className="flex-grow-1 border rounded-4 p-3 bg-white shadow-sm overflow-auto mb-3"
      >
        {messages.map((msg, idx) => (
          <motion.div
            key={idx}
            className={`mb-2 d-flex ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'}`}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <div
              className={`p-3 rounded-4 shadow-sm fw-normal fs-6 lh-base ${
                msg.role === 'user' ? 'bg-primary text-white' : 'bg-light text-dark'
              }`}
              style={{ maxWidth: '75%', whiteSpace: 'pre-wrap' }}
            >
              {msg.content}
            </div>
          </motion.div>
        ))}
        {loading && (
          <motion.div
            className="d-flex justify-content-start"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ repeat: Infinity, duration: 1, ease: "easeInOut" }}
          >
            <div className="bg-light text-dark rounded-4 shadow-sm p-3 fw-normal fs-6 lh-base" style={{ maxWidth: '75%' }}>
              <span className="typing-dots">● ● ●</span>
            </div>
          </motion.div>
        )}
        <div ref={chatEndRef} />
      </div>

      {isScrolledUp && (
        <div className="text-center mb-2">
          <button className="btn btn-outline-secondary btn-sm" onClick={() => {
            chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
            setIsScrolledUp(false);
          }}>
            ⬇ Scroll to bottom
          </button>
        </div>
      )}

      <div
        className="d-flex align-items-center justify-content-between rounded-4 px-4 py-3 shadow-sm border bg-white"
        style={{ gap: '1rem' }}
      >
        <input
          className="form-control border-0 bg-white fs-6"
          type="text"
          placeholder="Type your message here..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          disabled={loading}
          style={{ flexGrow: 1 }}
        />
        <button
          className="btn btn-primary px-4 py-2 rounded-3"
          onClick={handleSend}
          disabled={loading || !input.trim()}
        >
          Send
        </button>
      </div>

      <style>{`
        .typing-dots {
          display: inline-block;
          animation: blink 1s infinite steps(1);
        }

        @keyframes blink {
          0% { opacity: 0.2; }
          50% { opacity: 1; }
          100% { opacity: 0.2; }
        }
      `}</style>
    </div>
  );
}

export default ChatPanel;
