// components/PhotoSide.js
import React, { useState, useEffect } from 'react';

const API_KEY = 'pmNK8CZbn2lOEr8vN8OERavIp3CsrUkmKqVcx9wAte03zTloVzTv5EHX'; // ðŸ”‘ Ð²ÑÑ‚Ð°Ð²ÑŒ ÑÐ²Ð¾Ð¹ ÐºÐ»ÑŽÑ‡

function PhotoSide() {
  const [photoUrl, setPhotoUrl] = useState('');
  const [nextUrl, setNextUrl] = useState('');

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð½Ð¾Ð²Ð¾Ð³Ð¾ Ñ„Ð¾Ñ‚Ð¾
  const preloadPhoto = async () => {
    try {
      const res = await fetch(
        'https://api.pexels.com/v1/search?query=travel&per_page=1&page=' + Math.floor(Math.random() * 100),
        {
          headers: {
            Authorization: API_KEY,
          },
        }
      );
      const data = await res.json();
      const photo = data.photos?.[0];

      // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ñ†Ð¸Ñ Ð¿Ð¾ ÐºÐ°Ñ‡ÐµÑÑ‚Ð²Ñƒ
      if (photo && photo.width > 2000 && photo.height > 1200) {
        const img = new Image();
        img.src = photo.src.landscape;
        img.onload = () => {
          setNextUrl(photo.src.landscape);
        };
      }
    } catch (err) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ñ„Ð¾Ñ‚Ð¾:', err);
    }
  };

  // ÐŸÐµÑ€ÐµÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ðµ Ð½Ð° Ð½Ð¾Ð²Ð¾Ðµ Ñ„Ð¾Ñ‚Ð¾ (ÐµÑÐ»Ð¸ Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ð¾)
  const switchPhoto = () => {
    if (nextUrl) {
      setPhotoUrl(nextUrl);
      setNextUrl('');
    }
  };

  useEffect(() => {
    // ÐŸÐµÑ€Ð²Ð°Ñ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ°
    preloadPhoto();

    // Ð—Ð°Ð¿ÑƒÑÐºÐ°ÐµÐ¼ Ñ†Ð¸ÐºÐ»: preload ÐºÐ°Ð¶Ð´Ñ‹Ðµ 58 ÑÐµÐº, ÑÐ¼ÐµÐ½Ð° Ñ‡ÐµÑ€ÐµÐ· 60
    const preloadInterval = setInterval(preloadPhoto, 58000);
    const switchInterval = setInterval(switchPhoto, 60000);

    return () => {
      clearInterval(preloadInterval);
      clearInterval(switchInterval);
    };
  }, []);

  return (
    <div
      className="col-md-6 d-none d-md-block"
      style={{
        backgroundImage: `url(${photoUrl})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',
        transition: 'background-image 1s ease-in-out'
      }}
    />
  );
}
