import React, { useState, useEffect, useRef } from 'react';

const API_KEY = 'YOUR_PEXELS_API_KEY'; // üîÅ –í—Å—Ç–∞–≤—å —Å–≤–æ–π –∫–ª—é—á –æ—Ç Pexels

function PhotoSide() {
  const [photoUrl, setPhotoUrl] = useState('');
  const preloadTimeout = useRef(null);
  const intervalRef = useRef(null);

  const loadPhoto = async () => {
    try {
      const page = Math.floor(Math.random() * 100) + 1;
      const res = await fetch(
        `https://api.pexels.com/v1/search?query=travel&per_page=1&page=${page}`,
        {
          headers: {
            Authorization: API_KEY,
          },
        }
      );
      const data = await res.json();
      const photo = data.photos?.[0];

      if (
        photo &&
        photo.src?.landscape &&
        photo.width / photo.height < 1.8 // üîç —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏—è: –∏–∑–±–µ–≥–∞–µ–º —à–∏—Ä–æ–∫–æ—Ñ–æ—Ä–º–∞—Ç–Ω—ã—Ö —Ñ–æ—Ç–æ
      ) {
        const img = new Image();
        img.src = photo.src.landscape;

        img.onload = () => {
          setPhotoUrl(photo.src.landscape);
          console.log('‚úÖ New image loaded:', photo.src.landscape);

          // –ï—Å–ª–∏ –µ—â—ë –Ω–µ –∑–∞–ø—É—â–µ–Ω –∏–Ω—Ç–µ—Ä–≤–∞–ª ‚Äî –∑–∞–ø—É—Å—Ç–∏—Ç—å –µ–≥–æ
          if (!intervalRef.current) {
            intervalRef.current = setInterval(() => {
              loadPhoto();
            }, 60000); // –∫–∞–∂–¥—ã–µ 60 —Å–µ–∫
          }
        };
      } else {
        console.log('‚õî Bad aspect ratio, retrying...');
        loadPhoto(); // –ø—Ä–æ–±—É–µ–º –¥—Ä—É–≥–æ–µ —Ñ–æ—Ç–æ
      }
    } catch (err) {
      console.error('‚ùå –û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ñ–æ—Ç–æ:', err);
    }
  };

  useEffect(() => {
    // –ü–µ—Ä–≤–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ —Å –∑–∞–¥–µ—Ä–∂–∫–æ–π –≤ 200–º—Å, —á—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å –¥–µ—Ä–≥–∞–Ω–∏—è
    preloadTimeout.current = setTimeout(() => {
      loadPhoto();
    }, 200);

    return () => {
      clearTimeout(preloadTimeout.current);
      clearInterval(intervalRef.current);
    };
  }, []);

  return (
    <div
      className="col-md-6"
      style={{
        backgroundImage: photoUrl ? `url(${photoUrl})` : 'none',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',
        transition: 'background-image 1s ease-in-out',
      }}
    >
      {!photoUrl && (
        <div className="w-100 h-100 d-flex align-items-center justify-content-center bg-dark text-white">
          <p>–ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è...</p>
        </div>
      )}
    </div>
  );
}

export default PhotoSide;
