import React from 'react';
import { Card, CardContent } from "@/components/ui/card";
import { MapPin, Calendar, BedDouble, CloudSun } from "lucide-react";

const sampleData = [
  {
    day: 1,
    title: "Arrival in Almaty",
    activity: "Explore Almaty, enjoy local food",
    hotel: "Almaty Comfort Hostel",
    weather: "Sunny, 22°C"
  },
  {
    day: 2,
    title: "Local Market & Museums",
    activity: "Visit Green Market and museums",
    hotel: "Hotel Kazakhstan",
    weather: "Partly cloudy, 20°C"
  },
  {
    day: 3,
    title: "Mountain Adventure",
    activity: "Hiking in the Tian Shan mountains",
    hotel: "Mountain Eco Lodge",
    weather: "Clear, 18°C"
  }
];

function ItineraryPanel() {
  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold mb-4">Your Itinerary</h2>
      <div className="grid gap-4">
        {sampleData.map((item) => (
          <Card key={item.day} className="rounded-2xl shadow-md">
            <CardContent className="p-4 space-y-2">
              <div className="text-lg font-medium flex items-center gap-2">
                <Calendar className="w-5 h-5 text-primary" /> Day {item.day}: {item.title}
              </div>
              <div className="flex items-center gap-2 text-sm text-gray-700">
                <MapPin className="w-4 h-4" /> {item.activity}
              </div>
              <div className="flex items-center gap-2 text-sm text-gray-700">
                <BedDouble className="w-4 h-4" /> {item.hotel}
              </div>
              <div className="flex items-center gap-2 text-sm text-gray-700">
                <CloudSun className="w-4 h-4" /> {item.weather}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

export default ItineraryPanel;
