import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

function ChatPanel() {
  const [messages, setMessages] = useState([
    {
      role: 'model',
      content: "ðŸ‘‹ Hello! I'm your smart travel planner. Let's start step-by-step!\nWhere would you like to go?"
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages([...newMessages, { role: 'model', content: data.reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setMessages([...newMessages, { role: 'model', content: 'Error: could not get reply.' }]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="d-flex flex-column h-100">
      <h4 className="mb-3">LLaMA 3 AI Chat</h4>

      <div className="flex-grow-1 border rounded p-3 bg-white shadow-sm overflow-auto mb-3" style={{ maxHeight: '80vh' }}>
        <AnimatePresence initial={false}>
          {messages.map((msg, idx) => (
            <motion.div
              key={idx}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.3 }}
              className={`mb-3 px-3 py-2 rounded-3 ${msg.role === 'user' ? 'bg-primary text-white align-self-end' : 'bg-light text-dark align-self-start'}`}
              style={{ maxWidth: '75%', whiteSpace: 'pre-wrap' }}
            >
              {msg.content}
            </motion.div>
          ))}
          {loading && (
            <motion.div
              className="text-muted text-center my-2"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              ðŸŸ¡ðŸŸ¡ðŸŸ¡
            </motion.div>
          )}
        </AnimatePresence>
        <div ref={chatEndRef} />
      </div>

      <div className="d-flex gap-2">
        <input
          className="form-control rounded-pill px-4 py-2 shadow-sm"
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
        />
        <button
          className="btn btn-dark px-4 rounded-pill shadow-sm"
          onClick={handleSend}
          disabled={loading}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatPanel;
