import React from 'react';
//import './TripVisualizer.css'; // üëà –º–æ–∂–Ω–æ –ø–æ–¥–∫–ª—é—á–∏—Ç—å –∫–∞—Å—Ç–æ–º–Ω—ã–π —Å—Ç–∏–ª—å (–∏–ª–∏ Tailwind)

function TripVisualizer({ itinerary }) {
  if (!itinerary || itinerary.length === 0) {
    return <p className="text-muted">No itinerary data available.</p>;
  }

  return (
    <div className="trip-container">
      <h2 className="trip-title">üó∫ Your Itinerary</h2>
      <div className="trip-days">
        {itinerary.map(({ day, title, activity, hotel, weather }) => (
          <div key={day} className="trip-card">
            <div className="trip-day-label">Day {day}</div>
            <h5 className="trip-card-title">{title}</h5>
            <ul className="trip-details">
              <li>üìç <strong>Activity:</strong> {activity}</li>
              <li>üõè <strong>Hotel:</strong> {hotel}</li>
              <li>üå§ <strong>Weather:</strong> {weather}</li>
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TripVisualizer;
