import React, { useState } from 'react';

function ChatPanel() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages([...newMessages, { role: 'model', content: data.reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
    }
  };

  return (
    <div className="d-flex flex-column h-100">
      <h4 className="mb-3">LLaMA 3 AI Chat</h4>
      <div className="flex-grow-1 border rounded p-3 bg-white shadow-sm overflow-auto mb-3" style={{ maxHeight: '400px' }}>
        {messages.map((msg, idx) => (
          <div key={idx} className={`mb-2 ${msg.role === 'user' ? 'text-end' : 'text-start'}`}>
            <strong>{msg.role === 'user' ? 'You' : 'LLaMA 3'}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <div className="d-flex">
        <input
          className="form-control me-2"
          type="text"
          placeholder="Ask LLaMA 3 something..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
        />
        <button className="btn btn-primary" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

export default ChatPanel;
