import React, { useEffect } from 'react';
import { useUser } from '../context/UserContext';
import { useNavigate, useLocation } from 'react-router-dom';
import { Globe, Plane, BookOpenCheck } from 'lucide-react'; // –¥–ª—è –∏–∫–æ–Ω–æ–∫, –µ—Å–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ—à—å lucide

function HomePage() {
  const { user, setUser } = useUser();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const token = params.get('token');
    const username = params.get('username');

    if (token && username) {
      setUser({ token, username });
      localStorage.setItem('token', token);
      localStorage.setItem('username', username);
      navigate('/', { replace: true });
    }
  }, [location, navigate, setUser]);

  const handleCreateTrip = () => {
    navigate(user ? '/planner' : '/login');
  };

  return (
    <div className="max-w-4xl mx-auto px-4 py-10 text-center">
      <h1 className="text-4xl font-bold mb-2">Trip DVisor</h1>
      <h2 className="text-3xl font-semibold mb-4">
        Welcome{user ? `, ${user.username}` : ''}!
      </h2>

      <p className="text-gray-600 mb-6">
        Trip DVisor is your intelligent travel companion. Create, customize, and optimize your travel plans using smart AI suggestions.
      </p>

      <button
        className="bg-green-600 text-white text-lg px-6 py-3 rounded-xl hover:bg-green-700 transition mb-10"
        onClick={handleCreateTrip}
      >
        üó∫Ô∏è Create a New Trip
      </button>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-teal-100 rounded-xl p-6 flex flex-col items-center shadow">
          <Plane className="w-8 h-8 mb-2" />
          <span className="font-medium">Choose your destination</span>
        </div>
        <div className="bg-teal-100 rounded-xl p-6 flex flex-col items-center shadow">
          <Globe className="w-8 h-8 mb-2" />
          <span className="font-medium">AI plans step-by-step</span>
        </div>
        <div className="bg-teal-100 rounded-xl p-6 flex flex-col items-center shadow">
          <BookOpenCheck className="w-8 h-8 mb-2" />
          <span className="font-medium">Get your itinerary</span>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
