import React from 'react';
import { Link } from 'react-router-dom';
import './MobileMenu.css';

function MobileMenu({ isOpen, toggle, onLogout, isAuthenticated, goToPlanner }) {
  return (
    <div className={`mobile-menu ${isOpen ? 'open' : ''}`}>
      <div className="mobile-menu-header d-flex justify-content-between align-items-center p-3">
        <Link to="/" onClick={toggle} className="d-flex align-items-center">
          <img src="/logo.png" alt="Trip DVisor Logo" height="28" className="me-2" />
        </Link>
        <button className="btn-close" onClick={toggle} aria-label="Close menu"></button>
      </div>

      <div className="mobile-menu-body d-flex flex-column px-4 py-2">
        <button className="btn btn-link nav-link text-start" onClick={() => { goToPlanner(); toggle(); }}>
          <i className="bi bi-robot me-1" /> Planner
        </button>
        <Link className="nav-link text-start" to="/favorites" onClick={toggle}>
          <i className="bi bi-heart me-1" /> Favorites
        </Link>
        {isAuthenticated ? (
          <button className="btn btn-outline-danger btn-sm mt-3" onClick={() => { onLogout(); toggle(); }}>
            Sign Out
          </button>
        ) : (
          <Link className="btn btn-outline-primary btn-sm mt-3" to="/login" onClick={toggle}>
            Sign In
          </Link>
        )}
      </div>
    </div>
  );
}

export default MobileMenu;
