import React, { useEffect, useState, useRef } from 'react';
import { Card, Button } from 'react-bootstrap';
import MapPreview from '../components/MapPreview';
import TripSummaryFooter from '../components/TripSummaryFooter';

function TripVisualizer() {
  const [showFooter, setShowFooter] = useState(false);
  const mapRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => setShowFooter(entry.isIntersecting),
      { threshold: 0.3 }
    );
    if (mapRef.current) observer.observe(mapRef.current);
    return () => observer.disconnect();
  }, []);

  return (
    <div className="position-relative px-4 py-4" style={{ backgroundColor: '#f9f9f9' }}>
      {/* ...другие карточки остаются как есть... */}

      {/* Route Map */}
      <Card className="mb-4 shadow-sm" ref={mapRef}>
        <Card.Body>
          <h5 className="fw-bold mb-3">Route Map</h5>
          <MapPreview />
        </Card.Body>
      </Card>

      {/* Itinerary и Transfer & Transportation тоже остаются */}

      {/* Sticky Footer */}
      {showFooter && (
        <div className="position-fixed bottom-0 start-0 end-0 p-3 sticky-footer-animate" style={{ zIndex: 1030 }}>
          <TripSummaryFooter />
        </div>
      )}
    </div>
  );
}

export default TripVisualizer;
