import React, { useState, useEffect } from 'react';
import ChatPanel from '../components/ChatPanel';
import TripVisualizer from '../components/TripVisualizer';

function PlannerPage() {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [activeTab, setActiveTab] = useState('chat');

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  if (isMobile) {
    return (
      <div className="container-fluid px-3 py-3">
        <div className="btn-group w-100 mb-3" role="group">
          <button
            className={`btn btn-outline-primary ${activeTab === 'chat' ? 'active fw-bold border-bottom border-primary' : ''}`}
            onClick={() => setActiveTab('chat')}
          >
            üí¨ Chat
          </button>
          <button
            className={`btn btn-outline-primary ${activeTab === 'trip' ? 'active fw-bold border-bottom border-primary' : ''}`}
            onClick={() => setActiveTab('trip')}
          >
            üìç Trip
          </button>
        </div>

        <div className="border rounded-4 p-2 shadow-sm" style={{ minHeight: '70vh' }}>
          {activeTab === 'chat' ? <ChatPanel /> : <TripVisualizer />}
        </div>
      </div>
    );
  }

  return (
    <div className="d-flex main-content vh-100 overflow-hidden">
      <div className="w-50 d-flex flex-column border-end p-3 overflow-auto">
        <ChatPanel />
      </div>
      <div className="w-50 p-3 overflow-auto">
        <TripVisualizer />
      </div>
    </div>
  );
}

export default PlannerPage;
