import React from 'react';
import { Button } from 'react-bootstrap';

function TripHeader({ plan }) {
  if (!plan) return null;

  const {
    destination,
    dates,
    group_mode,
    interests
  } = plan;

  const dateLabel = dates ? `${dates[0]} ‚Äì ${dates[1]}` : 'Dates not set';
  const groupLabel = group_mode ? 'Group trip' : 'Solo traveler';

  return (
    <div className="d-flex justify-content-between align-items-start mb-4">
      <div>
        <h2 className="fw-bold">Trip to {destination}</h2>
        <div className="mb-2 text-muted">üìÖ {dateLabel} ‚Ä¢ üßç {groupLabel}</div>
        <div>
          {interests?.map((interest, i) => (
            <span key={i} className={`badge me-2 ${getBadgeStyle(interest)}`}>
              {capitalize(interest)}
            </span>
          ))}
        </div>
      </div>
      <div className="d-flex gap-2">
        <Button variant="outline-secondary" size="sm">üñä Modify</Button>
        <Button variant="outline-success" size="sm">üì• Download</Button>
        <Button variant="outline-info" size="sm">üîó Share</Button>
      </div>
    </div>
  );
}

// üîß Helper functions
const capitalize = str => str.charAt(0).toUpperCase() + str.slice(1);
const getBadgeStyle = keyword => {
  const map = {
    Museums: 'bg-primary',
    Cafes: 'bg-info text-dark',
    Food: 'bg-warning text-dark',
    Relax: 'bg-success',
    Adventure: 'bg-danger',
  };
  return map[keyword] || 'bg-secondary';
};

export default TripHeader;
