import React, { useEffect, useRef, useState } from 'react';

function ChatPanel() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  const handleSend = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { role: 'user', content: input }];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/api/ollama-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      setMessages([...newMessages, { role: 'model', content: data.reply }]);
    } catch (err) {
      console.error('Error sending message:', err);
      setMessages([...newMessages, { role: 'model', content: 'Error: could not get reply.' }]);
    } finally {
      setLoading(false);
    }
  };

  // 🔥 Статичное приветствие один раз при загрузке
useEffect(() => {
  const resetSession = async () => {
    await fetch('http://localhost:5000/api/ollama-reset', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' }
    });
  };

  resetSession();  // 👈 вызываем сброс контекста

  setMessages([{
    role: 'model',
    content: "👋 Hello! I'm your smart travel planner. Let's start step-by-step!\nWhere would you like to go?"
  }]);
}, []);

  // 📜 Автопрокрутка вниз
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="d-flex flex-column h-100">
      <h4 className="mb-3">LLaMA 3 AI Chat</h4>
      <div className="flex-grow-1 border rounded p-3 bg-white shadow-sm overflow-auto mb-3">
        {messages.map((msg, idx) => (
 <div key={idx} className={`mb-2 d-flex ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'}`}
>
  <div
    className={`p-2 rounded-3 shadow-sm ${
      msg.role === 'user' ? 'bg-primary text-white' : 'bg-light text-dark'
    }`}
    style={{ maxWidth: '75%', whiteSpace: 'pre-wrap' }}
  >
    {msg.content}
  </div>
</div>         
        ))}
        {loading && <div className="text-muted text-center mt-2">Thinking...</div>}
        <div ref={chatEndRef} />
      </div>
      <div className="d-flex">
        <input
          className="form-control me-2"
          type="text"
          placeholder="Type your answer..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
        />
        <button className="btn btn-primary" onClick={handleSend} disabled={loading}>Send</button>
      </div>
    </div>
  );
}

export default ChatPanel;
